mapper.select:
SELECT DISTINCT m.menu_puuid AS menu_puuid, m.menu_uuid AS menu_uuid, m.menu_name AS menu_name, m.menu_url AS menu_url, m.menu_icon AS menu_icon, m.menu_sort AS menu_sort, m.menu_ctrl AS menu_ctrl, a.status FROM ht_admin a INNER JOIN ht_admin_and_role anr ON a.id = anr.user_id INNER JOIN ht_admin_role anrr ON anr.role_id = anrr.id INNER JOIN ht_admin_role_menu_permissions mp ON anrr.id = mp.role_id AND mp.del_flag = '0' INNER JOIN ht_admin_menu m ON mp.menu_uuid = m.menu_uuid AND m.del_flag = '0' INNER JOIN ht_admin_permissions p ON p.permission_uuid = mp.permission_uuid AND p.del_flag = '0' AND m.menu_hide = '0' WHERE m.menu_puuid = '0' AND m.menu_hide = '0'
AND p.permission = #{permission, jdbcType=VARCHAR}
AND a.id = #{id, jdbcType=VARCHAR}
ORDER BY m.menu_sort ASC

SELECT DISTINCT a.id, a.username, a.truename, anr.role_id, a.state FROM ht_admin a LEFT JOIN ht_admin_and_role anr ON a.id = anr.user_id WHERE a.username = #{username, jdbcType=VARCHAR}
AND a.password = #{password, jdbcType=VARCHAR}
AND a.state = '0'
AND a.del_flag = '0'

update 
  ht_admin a
  set a.password=#{password, jdbcType=VARCHAR}
WHERE
  a.id = #{id, jdbcType=VARCHAR}
