mapper.select:
SELECT DISTINCT a.id, a.username, a.truename, anr.role_id, a.state FROM ht_admin a LEFT JOIN ht_admin_and_role anr ON a.id = anr.user_id WHERE a.username = #{username, jdbcType=VARCHAR}
AND a.password = #{password, jdbcType=VARCHAR}
AND a.state = '0'
AND a.del_flag = '0'

update 
  ht_admin a
  set a.password=#{password, jdbcType=VARCHAR}
WHERE
  a.id = #{id, jdbcType=VARCHAR}

SELECT DISTINCT a.id, a.username, a.truename, anr.role_id, a.state FROM ht_admin a LEFT JOIN ht_admin_and_role anr ON a.id = anr.user_id WHERE a.mobile = #{mobile, jdbcType=VARCHAR}
AND a.state = '0'
AND a.del_flag = '0'
