sql.where:
1=1


    AND w.id  !=  #{id}



    AND w.workname_id =#{businessId}



    AND n.work_name LIKE CONCAT( #{businessName}, '%')



    AND w.check_status = #{auditFlag}



    AND w.flow_status = #{processStatus}



    AND w.create_time >= str_to_date(CONCAT(#{createStartTime},' 00:00:00'),'%Y-%m-%d %H:%i:%s')



    AND w.create_time <=str_to_date(CONCAT(#{createEndTime},' 23:59:59'),'%Y-%m-%d %H:%i:%s')

mapper.select:
select w.id,w.workname_id businessId,n.work_name businessName, case when w.check_status=1 then '是' else '否' end auditFlagString, (select count(DISTINCT n.flow_order) from ht_workflow_node n where n.workflow_id=w.id) flowNode, w.mail_notifier mailWarningUser, date_format(w.create_time,'%Y-%m-%d %H:%i:%s')createTime, w.update_user updateUser, case when w.flow_status=1 then '正常' else '异常' end processStatusString, date_format(w.update_time,'%Y-%m-%d %H:%i:%s')updateTime from ht_workflow w inner join ht_workname n on w.workname_id=n.id
order by w.update_time desc
LIMIT #{limitStart} , #{limitEnd}

mapper.update:
UPDATE ht_workflow w
SET w.workname_id = #{businessId},w.flow_status=1,w.check_status=#{auditFlag},w.mail_notifier=#{mailWarningUser},w.update_user=#{updateUser}
WHERE  w.id = #{id}

mapper.select:
select a.id,a.truename,a.username,ar.role_name roleName,a.mobile,a.email,dep.name AS departmentname, DATE_FORMAT(a.create_time,'%Y-%m-%d %H:%i:%s')addTime,CONCAT(a.username,',',a.email)mailWarningUser from ht_admin a left join ht_admin_and_role r on a.id=r.user_id left join ht_admin_role ar on r.role_id=ar.id left join hyjf_user.ht_r_oa_department dep ON a.department_id = dep.id where a.del_flag = 0 and a.state=0
and a.username like CONCAT(#{username},'%')
and r.role_id in
#{role}
and a.username != #{creatUser}
order by a.username asc

select id,truename,email from ht_admin
WHERE del_flag = 0 and state=0 and id  in

    #{pojo.adminId}

select a.id,a.truename,a.email from ht_admin a inner join ht_admin_and_role r on a.id=r.user_id where a.del_flag=0 and a.state=0 and r.role_id in
#{pojo.adminId}
GROUP BY r.role_id
