  AND hdp.completion_time <= #{completionTimeSerachEnd}


  AND hdp.delivery_status = #{deliveryStatusSerach}


  AND hdp.recharge_state = #{rechargeStateSerach}


  AND hdp.processing_state = #{processingStateSerach}


