SELECT count(*) FROM ht_new_year_activity ya LEFT JOIN ht_new_year_activity_reward ar ON ya.user_id = ar.user_id LEFT JOIN hyjf_user.ht_user dus ON dus.user_id= ya.user_id LEFT JOIN hyjf_user.ht_user_info din ON din.user_id = ya.user_id
ya.invest_amount > 0
and dus.username = #{username}
and din.truename = #{truename}

SELECT ya.id AS id, dus.username AS userName, din.truename AS trueName, ya.invest_amount AS investAmount, ya.user_id userId, ar.reward AS reward FROM ht_new_year_activity ya LEFT JOIN ht_new_year_activity_reward ar ON ya.user_id = ar.user_id LEFT JOIN hyjf_user.ht_user dus ON dus.user_id= ya.user_id LEFT JOIN hyjf_user.ht_user_info din ON din.user_id = ya.user_id
ya.invest_amount > 0
and dus.username = #{username}
and din.truename = #{truename}
order by ya.invest_amount
DESC
${sort}
LIMIT #{limitStart} , #{limitEnd}

SELECT count(*) FROM ht_new_year_activity_reward ar LEFT JOIN ht_new_year_activity ya ON ya.user_id = ar.user_id LEFT JOIN hyjf_user.ht_user dus ON dus.user_id= ar.user_id LEFT JOIN hyjf_user.ht_user_info din ON din.user_id = ar.user_id
ar.reward != ''
and dus.username = #{username}
and din.truename = #{truename}
and ar.`status` = #{status}

SELECT ar.id, ar.user_id, dus.username as user_name, din.truename as true_name, ar.recipient_name, ar.recipient_address, ar.recipient_mobile, ar.reward, ar.`status`, ar.release_time, ar.get_time FROM ht_new_year_activity_reward ar LEFT JOIN ht_new_year_activity ya ON ya.user_id = ar.user_id LEFT JOIN hyjf_user.ht_user dus ON dus.user_id= ar.user_id LEFT JOIN hyjf_user.ht_user_info din ON din.user_id = ar.user_id
ar.reward != ''
and dus.username = #{username}
and din.truename = #{truename}
and ar.`status` = #{status}
order by ar.get_time ${sort}
order by ar.release_time ${sortTwo}
LIMIT #{limitStart} , #{limitEnd}
