sql.where:
hydu.open_account = 1
AND hydu.username LIKE CONCAT( #{userName,jdbcType=VARCHAR}, '%')
AND hydac.chinapnr_usrid = #{account,jdbcType=VARCHAR}
AND hydui.idcard LIKE CONCAT( #{idCard,jdbcType=VARCHAR}, '%')
AND hydui.attribute = #{userProperty,jdbcType=INTEGER}
AND hydui.truename LIKE CONCAT( #{realName,jdbcType=VARCHAR}, '%')
AND hydu.account_esb = #{openAccountPlat,jdbcType=VARCHAR}
AND hydac.addtime >= unix_timestamp(#{openTimeStart,jdbcType=VARCHAR})
AND hydac.addtime <= unix_timestamp(#{openTimeEnd,jdbcType=VARCHAR})

mapper.select:
SELECT h.user_id, h.username, h.truename AS real_name, h.idcard AS id_card, h.attribute, h.birthday, h.sex, h.idcard, concat( province,city,area) as regist_area, pna.`name` AS user_property, pnoap.`name` AS account_status_name, h.chinapnr_usrid AS account, h.chinapnr_usrcustid AS customer_account, pnoa.`name` AS open_account_plat, FROM_UNIXTIME( h.addtime, '%Y-%m-%d %H:%i:%s' ) AS open_time FROM (SELECT hydu.user_id, hydu.username, hydui.truename, hydui.idcard, hydui.attribute, hydui.birthday, hydui.sex, province, city, area, hydac.chinapnr_usrid, hydac.chinapnr_usrcustid, hydac.addtime, hydu.open_account, hydu.account_esb FROM ht_users hydu LEFT JOIN ht_users_info hydui ON hydu.user_id = hydui.user_id LEFT JOIN ht_account_chinapnr hydac ON hydac.user_id = hydu.user_id
ORDER BY hydac.addtime DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}
) h LEFT JOIN ht_param_name pna ON cast(h.attribute AS CHAR) = pna.name_cd AND pna.name_class = 'USER_PROPERTY' LEFT JOIN ht_param_name pnoap ON CAST(h.open_account AS CHAR) = pnoap.name_cd AND pnoap.name_class = 'ACCOUNT_STATUS' LEFT JOIN ht_param_name pnoa ON CAST(h.account_esb AS CHAR) = pnoa.name_cd AND pnoa.name_class = 'CLIENT'

mapper.update:
update ht_account
set
plan_frost = IFNULL(plan_frost,0) - #{planFrost,jdbcType=DECIMAL}
where
user_id = #{userId,jdbcType=INTEGER}

update ht_account
set
bank_balance_cash = IFNULL(bank_balance_cash,0) + #{bankBalanceCash,jdbcType=DECIMAL},

	plan_frost = IFNULL(plan_frost,0) + #{planFrost,jdbcType=DECIMAL}


	plan_balance = IFNULL(plan_balance,0) + #{planBalance,jdbcType=DECIMAL}

where
user_id = #{userId,jdbcType=INTEGER}

update ht_account
set
balance = IFNULL(balance,0) - #{balance,jdbcType=DECIMAL},
total = IFNULL(total,0) - #{total,jdbcType=DECIMAL}
where
user_id = #{userId,jdbcType=INTEGER}

update ht_account
set
balance = IFNULL(balance,0) + #{balance,jdbcType=DECIMAL},
frost = IFNULL(frost,0) - #{frost,jdbcType=DECIMAL}
where
user_id = #{userId,jdbcType=INTEGER}

update ht_account
set
plan_balance = IFNULL(plan_balance,0) + #{planBalance,jdbcType=DECIMAL}
where
user_id = #{userId,jdbcType=INTEGER}

update ht_account
set
plan_frost = IFNULL(plan_frost,0) - #{planFrost,jdbcType=DECIMAL},
plan_balance = IFNULL(plan_balance,0) + #{planBalance,jdbcType=DECIMAL}
where
user_id = #{userId,jdbcType=INTEGER}

update ht_account
set
plan_balance = IFNULL(plan_balance,0) + #{planBalance,jdbcType=DECIMAL},
plan_frost = IFNULL(plan_frost,0) - #{planFrost,jdbcType=DECIMAL}
where
user_id = #{userId,jdbcType=INTEGER}

update ht_account
set
plan_frost = IFNULL(plan_frost,0) + #{planFrost,jdbcType=DECIMAL},
plan_balance = IFNULL(plan_balance,0) - #{planBalance,jdbcType=DECIMAL}
where
user_id = #{userId,jdbcType=INTEGER}

update ht_account
set
plan_balance = IFNULL(plan_balance,0) + #{planBalance,jdbcType=DECIMAL}

	,plan_accede_balance = IFNULL(plan_accede_balance,0) +
	#{planAccedeBalance,jdbcType=DECIMAL}

where
user_id = #{userId,jdbcType=INTEGER}

mapper.select:
SELECT hydu.user_id, hydu.username, hydu.mobile, hydui.truename AS real_name, hydui.idcard AS id_card, pnoap.`name` AS account_status_name, hbc.card_no AS account, hboa.account AS customer_account, pnoa.`name` AS open_account_plat, date_format(hboa.create_time,'%Y-%m-%d %H:%i:%s') AS open_time FROM ht_users hydu LEFT JOIN ht_users_info hydui ON hydu.user_id = hydui.user_id INNER JOIN ht_bank_open_account hboa ON hboa.user_id = hydu.user_id LEFT JOIN ht_bank_card hbc ON hbc.user_id = hydu.user_id LEFT JOIN ht_param_name pnoap ON CAST(hydu.open_account AS CHAR) = pnoap.name_cd AND pnoap.name_class = 'ACCOUNT_STATUS' LEFT JOIN ht_param_name pnoa ON CAST(hydu.bank_account_esb AS CHAR) = pnoa.name_cd AND pnoa.name_class = 'CLIENT'
ORDER BY hboa.create_time DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}

mapper.update:
update ht_account
set
bank_total = IFNULL(bank_total,0) + #{bankTotal,jdbcType=DECIMAL},
bank_balance = IFNULL(bank_balance,0) + #{bankBalance,jdbcType=DECIMAL}
where
user_id = #{userId,jdbcType=INTEGER}

update ht_account
set
	bank_invest_sum = IFNULL(bank_invest_sum,0) + #{bankInvestSum,jdbcType=DECIMAL}
where
	user_id = #{userId,jdbcType=INTEGER}

update ht_account
set
	bank_interest_sum = IFNULL(bank_interest_sum,0) + IFNULL(plan_repay_interest,0) + #{bankInterestSum,jdbcType=DECIMAL}
where
	user_id = #{userId,jdbcType=INTEGER}

update ht_account
set
	bank_wait_repay = IFNULL(bank_wait_repay,0) + #{bankWaitRepay,jdbcType=DECIMAL}
where
	user_id = #{userId,jdbcType=INTEGER}

update ht_account
set
bank_total = IFNULL(bank_total,0) - #{bankTotal,jdbcType=DECIMAL},
bank_balance = IFNULL(bank_balance,0) - #{bankBalance,jdbcType=DECIMAL}
where
user_id = #{userId,jdbcType=INTEGER}

update ht_account
set
bank_total = IFNULL(bank_total,0) + #{bankTotal,jdbcType=DECIMAL},
bank_balance = IFNULL(bank_balance,0) + #{bankBalance,jdbcType=DECIMAL}
where
user_id = #{userId,jdbcType=INTEGER}
