mapper.select:
select sum(tmpcount) from ( SELECT COUNT(1) as tmpcount from ht_borrow_repay_plan AS rp INNER JOIN ht_borrow AS b ON rp.borrow_nid=b.borrow_nid INNER JOIN ht_borrow_info AS binfo on b.borrow_nid=binfo.borrow_nid Left join ht_borrow_apicron ba on rp.borrow_nid=ba.borrow_nid and rp.repay_period=ba.period_now and ba.api_type='1'
UNION ALL SELECT COUNT(1) as tmpcount from ht_borrow_repay AS rp INNER JOIN ht_borrow AS b ON rp.borrow_nid=b.borrow_nid and b.borrow_style in ('end','endday') INNER JOIN ht_borrow_info AS binfo on b.borrow_nid=binfo.borrow_nid Left join ht_borrow_apicron ba on rp.borrow_nid=ba.borrow_nid and rp.repay_period=ba.period_now and ba.api_type='1'
) t

select * from( select rp.nid AS nid, b.borrow_nid AS borrowNid, u.user_id AS userId, u.username AS borrowUserName, binfo.name AS borrowName, hic.inst_name AS instName, b.project_Type AS projectType, CASE WHEN b.project_Type = 0 THEN '汇保贷' WHEN b.project_Type = 1 THEN '汇典贷' WHEN b.project_Type = 2 THEN '汇小贷' WHEN b.project_Type = 3 THEN '汇车贷' WHEN b.project_Type = 4 THEN '新手标' END AS projectTypeName, b.borrow_Period AS borrowPeriod, b.borrow_Apr AS borrowApr, b.account AS borrowAccount, b.borrow_account_yes AS borrowAccountYes, bs.name AS repayType, rp.repay_period AS repayPeriod, rp.repay_capital AS repayCapital, rp.repay_interest AS repayInterest, rp.repay_account+rp.charge_interest+rp.late_interest+rp.late_interest_wait AS repayAccount, rp.repay_fee AS repayFee, rp.charge_days AS tiqiantianshu, rp.charge_interest AS shaohuanlixi, '' AS yanqitianshu, '' AS yanqilixi, rp.late_days AS yuqitianshu, if(rp.repay_status='1',rp.late_interest,rp.late_interest_wait) AS yuqilixi, rp.repay_account_yes AS yinghuanzonge, rp.charge_penalty_interest AS shihuanzonge, if(rp.repay_status=0 and rp.advance_status=3,2,rp.repay_status) AS status, if(rp.repay_action_time='' OR rp.repay_action_time='0' OR rp.repay_action_time='null', '',FROM_UNIXTIME(rp.repay_action_time,'%Y-%m-%d %H:%i:%s' )) AS repayActionTime, if(rp.repay_time='' OR rp.repay_time='0' OR rp.repay_time='null', '',FROM_UNIXTIME(rp.repay_time, '%Y-%m-%d' )) AS repayLastTime, if(rp.repay_action_time is null OR rp.repay_action_time='' OR rp.repay_action_time='0', '', CASE rp.repay_money_source WHEN 1 THEN '借款人还款' WHEN 2 THEN '机构垫付' WHEN 3 THEN '保证金垫付' ELSE '借款人还款' END ) as repayMoneySource, b.repay_account_capital-b.repay_account_capital_yes AS repayAccountCapitalWait, b.repay_account_interest_wait AS repayAccountInterestWait, b.plan_nid as planNid, rp.repay_account_yes as repayAccountYes, if(ba.status is null OR ba.status='', '未提交', CASE ba.status WHEN 1 THEN '还款请求中' WHEN 2 THEN '还款请求成功' WHEN 9 THEN '还款处理中' WHEN 3 THEN '还款校验成功' WHEN 4 THEN '还款校验失败' WHEN 5 THEN '还款失败' WHEN 6 THEN '还款成功' WHEN 7 THEN '还款请求失败' WHEN 8 THEN '还款部分成功' ELSE '待还款' END ) as extraYieldRepayStatus, ba.user_name as userName, ba.submitter, CASE rp.auto_repay when 0 then '手动还款' else '自动还款' end as autoRepay, b.verify_time, binfo.repay_org_name, b.verify_time as verifyTime, b.borrow_full_time as borrowFullTime, b.recover_last_time as recoverLastTime, rp.repay_capital_yes as repayAccountCapitalYes, rp.repay_interest_yes as repayAccountInterestYes, if(rp.repay_type='wait_yes',rp.repay_fee,0) as yihuanfuwufei, binfo.entrusted_user_name as entrustedUserName from ht_borrow_repay AS rp INNER JOIN ht_borrow AS b ON rp.borrow_nid=b.borrow_nid and b.borrow_style in ('end','endday') INNER JOIN ht_borrow_info AS binfo on b.borrow_nid=binfo.borrow_nid LEFT JOIN ht_borrow_style AS bs on b.borrow_style=bs.nid LEFT JOIN ht_r_user u ON b.user_id = u.user_id LEFT JOIN ht_hjh_inst_config hic ON hic.inst_code = binfo.inst_code Left join ht_borrow_apicron ba on rp.borrow_nid=ba.borrow_nid and rp.repay_period=ba.period_now and ba.api_type='1'
UNION ALL select rp.nid AS nid, b.borrow_nid AS borrowNid, u.user_id AS userId, u.username AS borrowUserName, binfo.name AS borrowName, hic.inst_name AS instName, b.project_Type AS projectType, CASE WHEN b.project_Type = 0 THEN '汇保贷' WHEN b.project_Type = 1 THEN '汇典贷' WHEN b.project_Type = 2 THEN '汇小贷' WHEN b.project_Type = 3 THEN '汇车贷' WHEN b.project_Type = 4 THEN '新手标' END AS projectTypeName, b.borrow_Period AS borrowPeriod, b.borrow_Apr AS borrowApr, b.account AS borrowAccount, b.borrow_account_yes AS borrowAccountYes, bs.name AS repayType, rp.repay_period AS repayPeriod, rp.repay_capital AS repayCapital, rp.repay_interest AS repayInterest, rp.repay_account+rp.charge_interest+rp.late_interest+rp.late_interest_wait AS repayAccount, rp.repay_fee AS repayFee, rp.charge_days AS tiqiantianshu, rp.charge_interest AS shaohuanlixi, '' AS yanqitianshu, '' AS yanqilixi, rp.late_days AS yuqitianshu, if(rp.repay_status='1',rp.late_interest,rp.late_interest_wait) AS yuqilixi, rp.repay_account_yes AS yinghuanzonge, rp.charge_penalty_interest AS shihuanzonge, if(rp.repay_status=0 and rp.advance_status=3,2,rp.repay_status) AS status, if(rp.repay_action_time='' OR rp.repay_action_time='0' OR rp.repay_action_time='null', '',FROM_UNIXTIME(rp.repay_action_time,'%Y-%m-%d %H:%i:%s' )) AS repayActionTime, if(rp.repay_time='' OR rp.repay_time='0' OR rp.repay_time='null', '',FROM_UNIXTIME(rp.repay_time, '%Y-%m-%d' )) AS repayLastTime, if(rp.repay_action_time is null OR rp.repay_action_time='' OR rp.repay_action_time='0', '', CASE rp.repay_money_source WHEN 1 THEN '借款人还款' WHEN 2 THEN '机构垫付' WHEN 3 THEN '保证金垫付' ELSE '借款人还款' END ) as repayMoneySource, b.repay_account_capital-b.repay_account_capital_yes AS repayAccountCapitalWait, b.repay_account_interest_wait AS repayAccountInterestWait, b.plan_nid as planNid, rp.repay_account_yes as repayAccountYes, if(ba.status is null OR ba.status='', '未提交', CASE ba.status WHEN 1 THEN '还款请求中' WHEN 2 THEN '还款请求成功' WHEN 9 THEN '还款处理中' WHEN 3 THEN '还款校验成功' WHEN 4 THEN '还款校验失败' WHEN 5 THEN '还款失败' WHEN 6 THEN '还款成功' WHEN 7 THEN '还款请求失败' WHEN 8 THEN '还款部分成功' ELSE '待还款' END ) as extraYieldRepayStatus, ba.user_name as userName, ba.submitter, CASE rp.auto_repay when 0 then '手动还款' else '自动还款' end as autoRepay, b.verify_time, binfo.repay_org_name, b.verify_time as verifyTime, b.borrow_full_time as borrowFullTime, b.recover_last_time as recoverLastTime, rp.repay_capital_yes as repayAccountCapitalYes, rp.repay_interest_yes as repayAccountInterestYes, if(rp.repay_type='wait_yes',rp.repay_fee,0) as yihuanfuwufei, binfo.entrusted_user_name as entrustedUserName from ht_borrow_repay_plan AS rp INNER JOIN ht_borrow AS b ON rp.borrow_nid=b.borrow_nid INNER JOIN ht_borrow_info AS binfo on b.borrow_nid=binfo.borrow_nid LEFT JOIN ht_borrow_style AS bs on b.borrow_style=bs.nid LEFT JOIN ht_r_user u ON b.user_id = u.user_id LEFT JOIN ht_hjh_inst_config hic ON hic.inst_code = binfo.inst_code Left join ht_borrow_apicron ba on rp.borrow_nid=ba.borrow_nid and rp.repay_period=ba.period_now and ba.api_type='1'
) tt ORDER BY tt.repayLastTime ASC
LIMIT #{limitStart} , #{limitEnd}

select sum(nid) from ( SELECT count(1) nid FROM ht_borrow AS b INNER JOIN ht_borrow_repay AS br ON b.borrow_nid = br.borrow_nid INNER JOIN ht_borrow_info hbi ON b.borrow_nid=hbi.borrow_nid LEFT JOIN ht_r_user u ON b.user_id = u.user_id LEFT JOIN ht_borrow_style AS bs ON b.borrow_style = bs.nid LEFT JOIN ht_borrow_project_type bpt ON bpt.borrow_cd = CAST(b.project_type AS CHAR) LEFT JOIN ht_borrow_apicron as ba ON b.borrow_nid = ba.borrow_nid and ba.api_type = 1 and ba.status <> 6 LEFT JOIN ht_hjh_inst_config hic ON hbi.inst_code = hic.inst_code
AND b.borrow_style in ('end','endday') UNION ALL SELECT count(1) nid FROM ht_borrow_repay_plan AS rp INNER JOIN ht_borrow AS b ON rp.borrow_nid = b.borrow_nid INNER JOIN ht_borrow_info hbi ON b.borrow_nid=hbi.borrow_nid LEFT JOIN ht_borrow_style AS bs ON b.borrow_style = bs.nid LEFT JOIN ht_r_user u ON b.user_id = u.user_id LEFT JOIN ht_hjh_inst_config hic ON hic.inst_code = hbi.inst_code LEFT JOIN ht_borrow_repay br ON br.borrow_nid = b.borrow_nid LEFT JOIN ht_borrow_project_type bpt ON bpt.borrow_cd = CAST(b.project_type AS CHAR)
) as count
