mapper.select:
SELECT br.nid AS nid, br.borrow_nid AS borrowNid, b.user_id AS userId, b.borrow_user_name AS borrowUserName, binfo.name AS borrowName, binfo.repay_org_name as repayOrgName, b.borrow_style AS borrowStyle, binfo.project_Type AS projectType, bpt.borrow_name AS projectTypeName, b.borrow_Period AS borrowPeriod, b.borrow_Apr AS borrowApr, b.account AS borrowAccount, b.borrow_account_yes AS borrowAccountYes, bs.name AS repayType, t.user_id AS recoverUserId, t.user_name AS recoverUserName, t.tender_user_attribute AS recover_user_attribute, t.invite_user_name AS referrer_name, t.invite_user_id AS referrer_user_id, t.invite_user_name AS referrer_true_name, br.recover_period AS recoverPeriod, t.account AS recoverTotal, br.recover_capital AS recoverCapital, br.recover_interest AS recoverInterest, br.recover_Account AS recoverAccount, br.recover_fee AS recoverFee, br.recover_capital_yes AS recoverCapitalYes, br.recover_interest_yes AS recoverInterestYes, br.recover_Account_yes AS recoverAccountYes, br.recover_capital-br.recover_capital_yes AS recoverCapitalWait, br.recover_interest-br.recover_interest_yes AS recoverInterestWait, br.recover_Account-br.recover_Account_yes AS recoverAccountWait, br.recover_status AS status, if(b.repay_last_time='' OR b.repay_last_time='0' OR b.repay_last_time='null','',FROM_UNIXTIME( b.repay_last_time, '%Y-%m-%d' )) AS recoverLastTime, if(br.recover_yestime is null or br.recover_yestime='', '',FROM_UNIXTIME(br.recover_yestime, '%Y-%m-%d %H:%i:%s' )) as repayActionTime, br.repay_ordid AS repay_ordid, br.repay_batch_no AS repay_batch_no, hic.inst_name AS instName, b.plan_nid FROM ht_borrow AS b INNER JOIN ht_borrow_info AS binfo ON binfo.borrow_nid=b.borrow_nid INNER JOIN ht_borrow_recover AS br ON br.borrow_nid=b.borrow_nid LEFT JOIN ht_borrow_style AS bs on b.borrow_style=bs.nid LEFT join ht_borrow_tender AS t on br.tender_id=t.id LEFT JOIN ht_borrow_project_type bpt ON bpt.borrow_cd = binfo.project_type LEFT JOIN ht_hjh_inst_config hic ON hic.inst_code = binfo.inst_code
ORDER BY br.id DESC
LIMIT #{limitStart} , #{limitEnd}

SELECT br.nid AS nid, br.borrow_nid AS borrowNid, b.user_id AS userId, b.borrow_user_name AS borrowUserName, binfo.name AS borrowName, binfo.repay_org_name as repayOrgName, b.borrow_style AS borrowStyle, binfo.project_Type AS projectType, bpt.borrow_name AS projectTypeName, b.borrow_Period AS borrowPeriod, b.borrow_Apr AS borrowApr, b.account AS borrowAccount, b.borrow_account_yes AS borrowAccountYes, bs.name AS repayType, t.user_id AS recoverUserId, t.user_name AS recoverUserName, br.invite_user_attribute AS recover_user_attribute, CASE WHEN br.invite_user_attribute = '1' THEN `od6`.`name` ELSE `od3`.`name` END `recover_region_name`, CASE WHEN br.invite_user_attribute = '1' THEN `od5`.`name` ELSE `od2`.`name` END `recover_branch_name`, CASE WHEN br.invite_user_attribute = '1' THEN `od4`.`name` ELSE `od`.`name` END `recover_department_name`, br.invite_user_name AS referrer_name, br.invite_user_id AS referrer_user_id, br.invite_user_name AS referrer_true_name, `od6`.`name` AS `referrer_region_name`, `od5`.`name` AS `referrer_branch_name`, `od4`.`name` AS `referrer_department_name`, br.recover_period AS recoverPeriod, t.account AS recoverTotal, br.recover_capital AS recoverCapital, br.recover_interest AS recoverInterest, br.recover_Account AS recoverAccount, br.recover_fee AS recoverFee, br.recover_capital_yes AS recoverCapitalYes, br.recover_interest_yes AS recoverInterestYes, br.recover_Account_yes AS recoverAccountYes, br.recover_capital-br.recover_capital_yes AS recoverCapitalWait, br.recover_interest-br.recover_interest_yes AS recoverInterestWait, br.recover_Account-br.recover_Account_yes AS recoverAccountWait, br.recover_status AS status, if(b.repay_last_time='' OR b.repay_last_time='0' OR b.repay_last_time='null','',FROM_UNIXTIME( b.repay_last_time, '%Y-%m-%d' )) AS recoverLastTime, if(br.recover_yestime is null or br.recover_yestime='', '',FROM_UNIXTIME(br.recover_yestime, '%Y-%m-%d %H:%i:%s' )) as repayActionTime, br.repay_ordid AS repay_ordid, br.repay_batch_no AS repay_batch_no, hic.inst_name AS instName, b.plan_nid, IFNULL(flg.order_id,rflg.order_id) AS freezeOrderId FROM ht_borrow AS b INNER JOIN ht_borrow_info AS binfo ON binfo.borrow_nid=b.borrow_nid INNER JOIN ht_borrow_recover AS br ON br.borrow_nid=b.borrow_nid LEFT JOIN ht_borrow_style AS bs on b.borrow_style=bs.nid LEFT join ht_borrow_tender AS t on br.tender_id=t.id LEFT JOIN ht_borrow_project_type bpt ON bpt.borrow_cd = binfo.project_type LEFT JOIN `ht_r_oa_users` `ou` ON `ou`.hyd_id = br.user_id AND ou.user_status IN ('E','Q1','Q11','Q2','Q21') LEFT JOIN `ht_r_oa_department` `od` ON `od`.`id` = `ou`.`departmentid` AND `od`.id IS NOT NULL LEFT JOIN `ht_r_oa_department` `od2` ON `od2`.`id` = `od`.`parentid` LEFT JOIN `ht_r_oa_department` `od3` ON `od3`.`id` = `od2`.`parentid` LEFT JOIN `ht_r_oa_users` `ou1` ON `ou1`.hyd_id = br.invite_user_id AND ou1.user_status IN ('E','Q1','Q11','Q2','Q21') LEFT JOIN `ht_r_oa_department` `od4` ON `od4`.`id` = `ou1`.`departmentid` AND `od4`.id IS NOT NULL LEFT JOIN `ht_r_oa_department` `od5` ON `od5`.`id` = `od4`.`parentid` LEFT JOIN `ht_r_oa_department` `od6` ON `od6`.`id` = `od5`.`parentid` LEFT JOIN ht_hjh_inst_config hic ON hic.inst_code = binfo.inst_code LEFT JOIN ht_bank_repay_org_freeze_log flg on flg.borrow_nid = b.borrow_nid and flg.del_flag = 0 LEFT JOIN ht_bank_repay_freeze_log rflg on rflg.borrow_nid = b.borrow_nid and rflg.del_flag = 0
ORDER BY br.id DESC
LIMIT #{limitStart} , #{limitEnd}
