mapper.select:
SELECT br.nid AS nid, br.borrow_nid AS borrowNid, b.user_id AS userId, b.borrow_user_name AS borrowUserName, binfo.name AS borrowName, binfo.repay_org_name as repayOrgName, b.borrow_style AS borrowStyle, binfo.project_Type AS projectType, bpt.borrow_name AS projectTypeName, b.borrow_Period AS borrowPeriod, b.borrow_Apr AS borrowApr, b.account AS borrowAccount, b.borrow_account_yes AS borrowAccountYes, bs.name AS repayType, t.user_id AS recoverUserId, t.user_name AS recoverUserName, t.tender_user_attribute AS recover_user_attribute, t.invite_user_name AS referrer_name, t.invite_user_id AS referrer_user_id, iu.truename AS referrer_true_name, br.recover_period AS recoverPeriod, t.account AS recoverTotal, br.recover_capital AS recoverCapital, br.recover_interest AS recoverInterest, br.recover_Account AS recoverAccount, br.recover_fee AS recoverFee, br.recover_capital_yes AS recoverCapitalYes, br.recover_interest_yes AS recoverInterestYes, br.recover_Account_yes AS recoverAccountYes, br.recover_capital-br.recover_capital_yes AS recoverCapitalWait, br.recover_interest-br.recover_interest_yes AS recoverInterestWait, br.recover_Account-br.recover_Account_yes AS recoverAccountWait, br.recover_status AS status, if(b.repay_last_time='' OR b.repay_last_time='0' OR b.repay_last_time='null','',FROM_UNIXTIME( b.repay_last_time, '%Y-%m-%d' )) AS recoverLastTime, if(br.recover_yestime is null or br.recover_yestime='', '',FROM_UNIXTIME(br.recover_yestime, '%Y-%m-%d %H:%i:%s' )) as repayActionTime, br.repay_ordid AS repay_ordid, br.repay_batch_no AS repay_batch_no, hic.inst_name AS instName, b.plan_nid FROM ht_borrow AS b INNER JOIN ht_borrow_info AS binfo ON binfo.borrow_nid=b.borrow_nid INNER JOIN ht_borrow_recover AS br ON br.borrow_nid=b.borrow_nid LEFT JOIN ht_borrow_style AS bs on b.borrow_style=bs.nid LEFT join ht_borrow_tender AS t on br.tender_id=t.id LEFT JOIN ht_r_user AS iu ON t.user_id=iu.user_id LEFT JOIN ht_borrow_project_type bpt ON bpt.borrow_cd = binfo.project_type LEFT JOIN ht_hjh_inst_config hic ON hic.inst_code = binfo.inst_code
ORDER BY br.id DESC
LIMIT #{limitStart} , #{limitEnd}
