sql.where:
(b.`status` = 4 OR b.`status` = 5 OR (b.`status`= 3 AND b.reverify_status <> 0))


	AND bt.borrow_nid LIKE CONCAT( #{borrowNidSrch}, '%')



	AND binfo.`name` LIKE CONCAT( #{borrowNameSrch}, '%')




	AND binfo.inst_code = #{instCodeSrch}




	AND bt.nid = #{orderNumSrch}



	AND bt.loan_ordid = #{loanOrdid}



	AND bt.user_name LIKE CONCAT( #{usernameSrch}, '%')



	AND hic.inst_code = #{instCodeOrgSrch}



	AND bt.`status` = #{isRecoverSrch}



	AND br.loan_batch_no LIKE CONCAT( #{loanBatchNo}, '%')



	AND UNIX_TIMESTAMP(bt.create_time) >= UNIX_TIMESTAMP(#{timeRecoverStartSrch})


	AND UNIX_TIMESTAMP(bt.create_time) <= UNIX_TIMESTAMP(#{timeRecoverEndSrch})+86399



	AND UNIX_TIMESTAMP(br.create_time) >= UNIX_TIMESTAMP(#{timeStartSrch})


	AND UNIX_TIMESTAMP(br.create_time) <= UNIX_TIMESTAMP(#{timeEndSrch})+86399



	AND b.plan_nid = #{planNidSrch}
