mapper.select:
SELECT COUNT(*) AS record_count FROM ht_hjh_accede hdpa LEFT JOIN ht_r_user ruser1 ON hdpa.invite_user_id = ruser1.user_id LEFT JOIN ht_hjh_plan hdp ON hdpa.plan_nid = hdp.plan_nid LEFT JOIN ht_coupon_real_tender hcrt ON hcrt.real_tender_id = hdpa.accede_order_id LEFT JOIN ht_coupon_tender hct ON hct.order_id = hcrt.coupon_tender_id LEFT JOIN ht_coupon_user hcu ON hcu.id = hct.coupon_grant_id LEFT JOIN ht_coupon_config hcc ON hcc.coupon_code = hcu.coupon_code LEFT JOIN ht_tender_agreement hta ON hta.tender_nid = hdpa.accede_order_id LEFT JOIN ht_r_user ruser2 ON ruser2.user_id = hdpa.user_id LEFT JOIN ht_r_oa_users ou ON ou.hyd_id = hdpa.invite_user_id AND ou.user_status IN ('E', 'Q1', 'Q11', 'Q2', 'Q21') LEFT JOIN ht_r_oa_department od ON od.id = ou.departmentid AND od.id IS NOT NULL LEFT JOIN ht_r_oa_department od2 ON od2.id = od.parentid LEFT JOIN ht_r_oa_department od3 ON od3.id = od2.parentid LEFT JOIN ht_r_oa_users oux ON oux.hyd_id = hdpa.user_id AND oux.user_status IN ('E', 'Q1', 'Q11', 'Q2', 'Q21') LEFT JOIN ht_r_oa_department odx ON odx.id = oux.departmentid AND odx.id IS NOT NULL LEFT JOIN ht_r_oa_department odx2 ON odx2.id = odx.parentid LEFT JOIN ht_r_oa_department odx3 ON odx3.id = odx2.parentid
AND hdp.plan_nid LIKE CONCAT('%', #{debtPlanNidSrch}, '%')
AND hdpa.accede_order_id = #{accedeOrderIdSrch}
AND hdpa.user_name LIKE CONCAT( #{userNameSrch},'%')
AND hdpa.lock_period = #{debtLockPeriodSrch}
AND ruser1.username LIKE CONCAT( #{refereeNameSrch},'%')
AND hdpa.order_status LIKE CONCAT( #{orderStatus},'%')
AND hdpa.client = #{platformSrch}
AND hdpa.create_time >= STR_TO_DATE(#{searchStartDate}, '%Y-%m-%d %H:%i:%s')
AND hdpa.create_time <= STR_TO_DATE(CONCAT(#{searchEndDate}, ' 23:59:59'),'%Y-%m-%d %H:%i:%s')
AND hdpa.count_interest_time >= UNIX_TIMESTAMP(#{countInterestTimeStartDate})
AND hdpa.count_interest_time <= UNIX_TIMESTAMP(CONCAT(#{countInterestTimeEndDate},' 23:59:59'))
AND hdpa.match_dates = #{matchDatesSrch}
AND hdpa.invest_counts = #{investCountsSrch}
and hdpa.end_date >= CONCAT(#{endDateStartSrch},' 00:00:00')
and hdpa.end_date <= CONCAT(#{endDateEndSrch},' 23:59:59')
AND FROM_UNIXTIME( hdpa.acctual_payment_time, '%Y-%m-%d' ) >= #{acctualPaymentTimeStartSrch}
AND FROM_UNIXTIME( hdpa.acctual_payment_time, '%Y-%m-%d' ) <= #{acctualPaymentTimeEndSrch}

SELECT SUM(hdpa.accede_account) AS accede_account, SUM(hdpa.already_invest) AS already_invest, SUM(hdpa.wait_total) AS wait_total, SUM(hdpa.wait_captical) AS wait_captical, SUM(hdpa.wait_interest) AS wait_interest,
SUM(hdpa.available_invest_account) AS sumAvailableInvestAccount, SUM(hdpa.frost_account) AS sumFrostAccount, CASE WHEN hdpa.order_status =5 OR hdpa.order_status =7 THEN SUM(hdpa.fair_value) ELSE 0 END AS sumFairValue
FROM ht_hjh_accede hdpa LEFT JOIN ht_r_user ruser1 ON hdpa.invite_user_id = ruser1.user_id LEFT JOIN ht_hjh_plan hdp ON hdpa.plan_nid = hdp.plan_nid LEFT JOIN ht_coupon_real_tender hcrt ON hcrt.real_tender_id = hdpa.accede_order_id LEFT JOIN ht_coupon_tender hct ON hct.order_id = hcrt.coupon_tender_id LEFT JOIN ht_coupon_user hcu ON hcu.id = hct.coupon_grant_id LEFT JOIN ht_coupon_config hcc ON hcc.coupon_code = hcu.coupon_code LEFT JOIN ht_tender_agreement hta ON hta.tender_nid = hdpa.accede_order_id LEFT JOIN ht_r_user ruser2 ON ruser2.user_id = hdpa.user_id LEFT JOIN ht_r_oa_users ou ON ou.hyd_id = hdpa.invite_user_id AND ou.user_status IN ('E', 'Q1', 'Q11', 'Q2', 'Q21') LEFT JOIN ht_r_oa_department od ON od.id = ou.departmentid AND od.id IS NOT NULL LEFT JOIN ht_r_oa_department od2 ON od2.id = od.parentid LEFT JOIN ht_r_oa_department od3 ON od3.id = od2.parentid LEFT JOIN ht_r_oa_users oux ON oux.hyd_id = hdpa.user_id AND oux.user_status IN ('E', 'Q1', 'Q11', 'Q2', 'Q21') LEFT JOIN ht_r_oa_department odx ON odx.id = oux.departmentid AND odx.id IS NOT NULL LEFT JOIN ht_r_oa_department odx2 ON odx2.id = odx.parentid LEFT JOIN ht_r_oa_department odx3 ON odx3.id = odx2.parentid
AND hdp.plan_nid LIKE CONCAT('%', #{debtPlanNidSrch}, '%')
AND hdpa.accede_order_id = #{accedeOrderIdSrch}
AND hdpa.user_name LIKE CONCAT( #{userNameSrch},'%')
AND ruser1.username LIKE CONCAT( #{refereeNameSrch},'%')
AND hdpa.order_status LIKE CONCAT( #{orderStatus},'%')
AND hdpa.client = #{platformSrch}
AND hdpa.lock_period = #{debtLockPeriodSrch}
AND hdpa.create_time >= STR_TO_DATE(#{searchStartDate}, '%Y-%m-%d %H:%i:%s')
AND hdpa.create_time <= STR_TO_DATE(CONCAT(#{searchEndDate}, ' 23:59:59'),'%Y-%m-%d %H:%i:%s')
AND hdpa.count_interest_time >= UNIX_TIMESTAMP(#{countInterestTimeStartDate})
AND hdpa.count_interest_time <= UNIX_TIMESTAMP(CONCAT(#{countInterestTimeEndDate},' 23:59:59'))
AND hdpa.match_dates = #{matchDatesSrch}
AND hdpa.invest_counts = #{investCountsSrch}
and hdpa.end_date >= CONCAT(#{endDateStartSrch},' 00:00:00')
and hdpa.end_date <= CONCAT(#{endDateEndSrch},' 23:59:59')
AND FROM_UNIXTIME( hdpa.acctual_payment_time, '%Y-%m-%d' ) >= #{acctualPaymentTimeStartSrch}
AND FROM_UNIXTIME( hdpa.acctual_payment_time, '%Y-%m-%d' ) <= #{acctualPaymentTimeEndSrch}
