mapper.select:
SELECT hydb.id as id, hydb.borrow_nid, hydb.tender_account_min, hydb.status AS borrow_status, hydb.reverify_status AS project_status, hydb.borrow_user_name borrow_user_name, hydb.applicant applicant, hydb.tender_account_max, hydbpt.borrow_class AS project_type, hydbpt.borrow_cd AS type, hydbpt.borrow_name AS type_name, hydb.`name` AS borrow_name, hydbs.`name` AS repay_style, hydb.project_name as project_name, hydb.borrow_contents, hydb.borrow_measures_mea, hydb.is_new, hydb.finance_purpose, hydb.monthly_income, hydb.payment, hydb.first_payment, hydb.second_payment, hydb.cost_introdution, hydb.fiance_condition, hydb.borrow_style,
SUBSTRING(FORMAT(hydb.account,4),1,LENGTH(FORMAT(hydb.account,4))-2) AS account, hydb.company_or_personal AS com_or_per, CASE WHEN hydb.company_or_personal = 2 THEN ( SELECT SUBSTRING(FORMAT(m.credit,4),1,LENGTH(FORMAT(m.credit,4))-2) FROM ht_borrow_maninfo m WHERE m.borrow_nid = hydb.borrow_nid ) ELSE ( SELECT SUBSTRING(FORMAT(u.credit,4),1,LENGTH(FORMAT(u.credit,4))-2) FROM ht_borrow_users u WHERE u.borrow_nid = hydb.borrow_nid ) END AS user_credit, hydb.account AS borrow_account, hydb.borrow_apr, CASE WHEN hydb.borrow_style = 'endday' THEN CONCAT(hydb.borrow_period, '') ELSE CONCAT( hydb.borrow_period, '' ) END AS borrow_period, CASE WHEN hydb.borrow_style = 'endday' THEN '天' ELSE '个月' END AS borrow_period_type, SUBSTRING(FORMAT(hydb.borrow_account_wait,4),1,LENGTH(FORMAT(hydb.borrow_account_wait,4))-2) AS invest_account, hydb.borrow_account_scale AS borrow_schedule, FROM_UNIXTIME(hydb.verify_time,'%Y-%m-%d %H:%i' ) AS send_time, FROM_UNIXTIME(hydb.borrow_full_time,'%Y-%m-%d %H:%i' ) AS full_time, CASE WHEN hydb.`status` = 1 AND hydb.verify_status = 3 THEN FROM_UNIXTIME( hydb.ontime, '%Y-%m-%d %H:%i:%s' ) ELSE FROM_UNIXTIME( hydb.verify_time, '%Y-%m-%d %H:%i' ) END AS on_time, CASE WHEN hydb.`status` = 1 AND hydb.verify_status = 3 THEN '10' WHEN hydb.`status` = 2 AND hydb.borrow_full_status = 0 AND ( hydb.verify_time + ( hydb.borrow_valid_time * 24 * 60 * 60 ) > UNIX_TIMESTAMP(NOW()) ) THEN '11' WHEN hydb.`status` = 3 THEN '12' WHEN hydb.`status` = 4 THEN '13' WHEN hydb.`status` = 5 THEN '14'
ELSE '' END AS `status`, hydb.borrow_increase_money increase_money, hydb.borrow_interest_coupon interest_coupon, hydb.borrow_taste_money taste_money, hydb.borrow_asset_number, hydb.borrow_project_source, hydb.borrow_interest_time, hydb.borrow_due_time, hydb.borrow_safeguard_way, hydb.borrow_income_description, hydb.borrow_publisher, hydb.borrow_extra_yield, hydb.contract_period, truncate(hydb.account/10000,2) AS borrow_account_wan, truncate(hydb.tender_account_min/10000,2) tender_account_min_wan, FROM_UNIXTIME( hydb.recover_last_time, '%Y年%m月%d日' ) recover_last_time, FROM_UNIXTIME( re.create_time, '%Y-%m-%d %H:%i' ) verify_over_time, hydb.borrow_level FROM ht_borrow hydb LEFT JOIN ht_borrow_project_type hydbpt ON hydbpt.borrow_cd = hydb.project_type LEFT JOIN ht_borrow_style hydbs ON hydbs.nid = hydb.borrow_style LEFT JOIN ht_borrow_repay re ON re.borrow_nid = hydb.borrow_nid WHERE ( hydbpt.borrow_project_type = 'HZT' OR hydbpt.borrow_project_type = 'HXF' ) AND ( ( ( hydb.`status` = 1 AND hydb.verify_status = 3 ) ) OR ( hydb.`status` = 2 AND hydb.borrow_full_status = 0 AND ( hydb.verify_time + ( hydb.borrow_valid_time * 24 * 60 * 60 ) > UNIX_TIMESTAMP(NOW()) ) ) OR ( hydb.`status` = 3 ) OR ( hydb.`status` = 4 ) OR ( hydb.`status` = 5 )
) AND hydb.borrow_nid =#{borrowNid,jdbcType=VARCHAR}

SELECT CONCAT( substring(hydcl.person_name, 1, 1), '**' ) AS `name`, CONCAT( substring(hydcl.ident, 1, 6), '******', substring(hydcl.ident, 15, 4) ) AS id_card, SUBSTRING(FORMAT(hydcl.credit_amount,4),1,LENGTH(FORMAT(hydcl.credit_amount,4))-2) AS account FROM ht_consume_list hydcl LEFT JOIN ht_borrow_info hydb ON hydb.consume_id = hydcl.consume_id
hydb.borrow_nid = #{borrowNid,jdbcType=VARCHAR}
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}

SELECT CONCAT(substring(hydbt.user_name,1,1),'**') AS user_name, SUBSTRING(FORMAT(hydbt.account,4),1,LENGTH(FORMAT(hydbt.account,4))-2) AS account, hydbt.`client` AS client, hydbt.`client` AS client_name, date_format(hydbt.create_time, '%Y-%m-%d %H:%i:%s') AS invest_time FROM ht_borrow_tender hydbt -- LEFT JOIN ht_r_user hydu ON hydbt.user_id = hydu.user_id
hydbt.borrow_nid = #{borrowNid,jdbcType=VARCHAR}
ORDER BY hydbt.create_time DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}
