AND hha.order_status <>7
AND hha.order_status = 7
UNION ALL SELECT hbtc.id FROM ht_borrow_tender_cpn hbtc INNER JOIN ht_hjh_plan hhp ON hbtc.borrow_nid = hhp.plan_nid LEFT JOIN ht_coupon_recover hcr ON hbtc.nid=hcr.tender_id INNER JOIN ht_coupon_tender hct ON hct.order_id = hbtc.nid INNER JOIN ht_coupon_user hcu ON hct.coupon_grant_id = hcu.id INNER JOIN ht_coupon_config hcc ON hcu.coupon_code = hcc.coupon_code WHERE hbtc.user_id= #{userId}
AND hcr.recover_status=0
AND hcr.recover_status=1
) t

