SELECT t.* FROM (
SELECT borrow.borrow_nid AS project_name, borrow.borrow_nid AS borrow_nid, CONCAT(borrow.borrow_apr, '%') borrow_apr, FROM_UNIXTIME(IFNULL(rec.recover_time,re.recover_time), '%Y-%m-%d' ) AS invest_date, IFNULL(rec.recover_time,re.recover_time) as orderby_invest_date, CASE WHEN hbc.credit_capital_assigned IS NOT NULL THEN 1 ELSE 0 END type, 0 coupon_type, IFNULL(rec.recover_capital_wait,ten.account)+ IFNULL( rec.recover_interest_wait, ten.recover_account_interest )- (SELECT IFNULL(SUM(ct.assign_interest - ct.assign_repay_interest),0) FROM ht_credit_tender ct WHERE rec.nid = ct.credit_tender_nid )- (SELECT IFNULL(SUM(ct.assign_capital - ct.assign_repay_capital),0) FROM ht_credit_tender ct WHERE rec.nid = ct.credit_tender_nid ) total_wait, '' AS coupon_name, CASE WHEN borrow.borrow_style = 'endday' THEN CONCAT(borrow.borrow_period, '天') ELSE CONCAT(borrow.borrow_period, '个月') END borrow_period, ten.nid as order_id, '' as assign_nid, '' as order_status FROM ht_borrow_tender ten LEFT JOIN ht_borrow_recover_plan rec ON ten.nid = rec.nid AND rec.recover_account > rec.credit_amount AND rec.recover_account_wait> 0 INNER JOIN ht_borrow_recover re ON ten.nid = re.nid AND re.recover_account > re.credit_amount AND re.recover_account_wait> 0 INNER JOIN ht_borrow borrow ON borrow.borrow_nid = ten.borrow_nid LEFT JOIN (SELECT hbc.tender_nid, MIN(hbc.credit_capital_assigned) credit_capital_assigned, MIN(hbc.credit_status) credit_status FROM ht_borrow_credit hbc GROUP BY hbc.tender_nid ) hbc ON ten.nid=hbc.tender_nid WHERE borrow.`status` = 4 AND ten.user_id= #{userId} AND ten.account>IFNULL(hbc.credit_capital_assigned,0) AND borrow.plan_nid IS NULL
AND FROM_UNIXTIME(IFNULL(rec.recover_time,re.recover_time),'%Y') = #{tradeYear,jdbcType=INTEGER}
AND FROM_UNIXTIME(IFNULL(rec.recover_time,re.recover_time),'%m') = #{tradeMonth,jdbcType=INTEGER}
UNION ALL SELECT borrow.borrow_nid AS project_name, borrow.borrow_nid AS borrow_nid, CONCAT(borrow.borrow_apr, '%') borrow_apr, FROM_UNIXTIME(borrow.repay_last_time, '%Y-%m-%d' ) AS invest_date, borrow.repay_last_time, 2 AS type, 0 coupon_type, (ten.assign_capital+ten.assign_interest) total_wait, '承接债转' AS coupon_name, CASE WHEN borrow.borrow_style = 'endday' THEN CONCAT(borrow.borrow_period, '天') ELSE CONCAT(borrow.borrow_period, '个月') END borrow_period, ten.credit_tender_nid as order_id, ten.assign_nid, '' as order_status FROM ht_credit_tender ten INNER JOIN ht_borrow borrow ON borrow.borrow_nid = ten.bid_nid WHERE ten.user_id= #{userId} AND borrow.`status` = 4 AND ten.assign_interest>0 AND borrow.plan_nid IS NULL
AND FROM_UNIXTIME(borrow.repay_last_time,'%Y') = #{tradeYear,jdbcType=INTEGER}
AND FROM_UNIXTIME(borrow.repay_last_time,'%m') = #{tradeMonth,jdbcType=INTEGER}
UNION ALL SELECT hb.borrow_nid AS project_name, hb.borrow_nid AS borrow_nid, CASE WHEN hcc.coupon_type = 2 THEN CONCAT(hcc.coupon_quota, '%') ELSE CONCAT(hb.borrow_apr, '%') END borrow_apr, FROM_UNIXTIME(hcr.recover_time, '%Y-%m-%d' ) AS recover_time, hcr.recover_time, 3 TYPE, hcc.coupon_type coupon_type, IFNULL(hcr.recover_account,0) total_wait, CASE WHEN hcc.coupon_type = '1' THEN '体验金' WHEN hcc.coupon_type = '2' THEN '加息券' WHEN hcc.coupon_type = '3' THEN '代金券' END AS coupon_name, CASE WHEN hb.borrow_style = 'endday' THEN CONCAT(hb.borrow_period, '天') ELSE CONCAT(hb.borrow_period, '个月') END borrow_period, hbtc.nid AS order_id, '' AS assign_nid, '' as order_status FROM ht_borrow_tender_cpn hbtc INNER JOIN ht_borrow hb ON hbtc.borrow_nid = hb.borrow_nid LEFT JOIN ht_coupon_recover hcr ON hbtc.nid=hcr.tender_id INNER JOIN ht_coupon_tender hct ON hct.order_id = hbtc.nid INNER JOIN ht_coupon_user hcu ON hct.coupon_grant_id = hcu.id INNER JOIN ht_coupon_config hcc ON hcu.coupon_code = hcc.coupon_code WHERE hbtc.user_id= #{userId} AND hb.plan_nid IS NULL AND hcr.recover_status =0 AND hcr.recover_account >0
AND FROM_UNIXTIME(hcr.recover_time,'%Y') = #{tradeYear,jdbcType=INTEGER}
AND FROM_UNIXTIME(hcr.recover_time,'%m') = #{tradeMonth,jdbcType=INTEGER}
UNION ALL SELECT borrow.borrow_nid AS project_name, borrow.borrow_nid AS borrow_nid, CONCAT(hiii.borrow_extra_yield, '%') borrow_apr, FROM_UNIXTIME(borrow.repay_last_time, '%Y-%m-%d' ) AS invest_date, borrow.repay_last_time, 4 AS type, 0 coupon_type, hiii.repay_interest_wait AS total_wait, '' AS coupon_name, CASE WHEN borrow.borrow_style = 'endday' THEN CONCAT(borrow.borrow_period, '天') ELSE CONCAT(borrow.borrow_period, '个月') END borrow_period, hiii.tender_nid as order_id, '' as assign_nid, '' as order_status FROM ht_increase_interest_invest hiii INNER JOIN ht_borrow borrow ON borrow.borrow_nid = hiii.borrow_nid INNER JOIN (select * from ht_increase_interest_loan where user_id=#{userId} ) hiil ON hiil.invest_order_id=hiii.order_id INNER JOIN ht_borrow_project_type hydbpt ON hydbpt.borrow_cd = borrow.project_type LEFT JOIN ht_borrow_credit hbc ON hiii.borrow_nid=hbc.tender_nid WHERE borrow.`status` = 4 AND hiii.user_id=#{userId} AND borrow.plan_nid IS NULL
AND FROM_UNIXTIME(borrow.repay_last_time,'%Y') = #{tradeYear,jdbcType=INTEGER}
