UNION ALL SELECT hhp.plan_name,
hhp.plan_name AS borrow_nid, CONCAT(hha.expect_apr, '%') borrow_apr, IF(IFNULL(hha.quit_time,0)=0,'--',FROM_UNIXTIME(hha.quit_time, '%Y-%m-%d' )) AS quit_time, hha.last_payment_time, 5 AS type, 0 coupon_type, hha.wait_total wait_total, '' AS coupon_name, CASE WHEN hhp.borrow_style = 'endday' THEN CONCAT(hhp.lock_period, '天') ELSE CONCAT(hhp.lock_period, '个月') END borrow_period, hha.accede_order_id as order_id, '' as assign_nid, hha.order_status order_status FROM ht_hjh_accede hha LEFT JOIN ht_hjh_plan hhp ON hha.plan_nid=hhp.plan_nid WHERE hha.order_status in (3,5) AND hha.user_id=#{userId}
AND FROM_UNIXTIME(hha.last_payment_time,'%Y') = #{tradeYear,jdbcType=INTEGER}
AND FROM_UNIXTIME(hha.last_payment_time,'%m') = #{tradeMonth,jdbcType=INTEGER}
UNION ALL SELECT hhp.plan_name AS project_name, hbtc.borrow_nid AS borrow_nid, CASE WHEN hcc.coupon_type = 2 THEN CONCAT(hcc.coupon_quota, '%') ELSE CONCAT(hhp.expect_apr, '%') END borrow_apr, FROM_UNIXTIME(hcr.recover_time, '%Y-%m-%d' ) AS recover_time, hcr.recover_time, 6 type, hcc.coupon_type coupon_type, IFNULL(hcr.recover_account,0) total_wait, CASE WHEN hcc.coupon_type = '1' THEN '体验金' WHEN hcc.coupon_type = '2' THEN '加息券' WHEN hcc.coupon_type = '3' THEN '代金券' END AS coupon_name, CASE WHEN hhp.borrow_style = 'endday' THEN CONCAT(hhp.lock_period, '天') ELSE CONCAT(hhp.lock_period, '个月') END borrow_period, hbtc.nid as order_id, '' as assign_nid, hha.order_status order_status FROM ht_borrow_tender_cpn hbtc INNER JOIN ht_hjh_plan hhp ON hbtc.borrow_nid = hhp.plan_nid left join ht_coupon_real_tender hcrt on hbtc.nid = hcrt.coupon_tender_id left join ht_hjh_accede hha ON hcrt.real_tender_id = hha.accede_order_id LEFT JOIN ht_coupon_recover hcr ON hbtc.nid=hcr.tender_id INNER JOIN ht_coupon_tender hct ON hct.order_id = hbtc.nid INNER JOIN ht_coupon_user hcu ON hct.coupon_grant_id = hcu.id INNER JOIN ht_coupon_config hcc ON hcu.coupon_code = hcc.coupon_code WHERE hbtc.user_id= #{userId} AND hcr.recover_status=0
AND FROM_UNIXTIME(hcr.recover_time,'%Y') = #{tradeYear,jdbcType=INTEGER}
AND FROM_UNIXTIME(hcr.recover_time,'%m') = #{tradeMonth,jdbcType=INTEGER}
) t ORDER BY t.invest_date ASC,t.order_id ASC
LIMIT #{limitStart} , #{limitEnd}
