sql.where:
fl.del_flag = 0


	AND fl.borrow_nid = #{borrowNid}


	AND fl.inst_code = #{instCode}


	AND fl.plan_nid = #{planNid}


	AND fl.order_id = #{orderId}


	AND fl.borrow_user_name LIKE CONCAT('%', #{borrowUserName}, '%')


	AND fl.repay_user_name LIKE CONCAT('%', #{repayUserName}, '%')


	AND FROM_UNIXTIME( fl.create_time, '%Y-%m-%d' ) >= #{submitTimeStartSrch}


	AND FROM_UNIXTIME( fl.create_time, '%Y-%m-%d' ) <= #{submitTimeEndSrch}

mapper.select:
SELECT fl.borrow_nid AS borrowNid, fl.borrow_period AS borrowPeriod, fl.total_period AS totalPeriod, fl.repay_user_name AS repayUserName, fl.borrow_user_name AS borrowUserName, fl.inst_code AS instCode, ic.inst_name AS instName, fl.plan_nid AS planNid, fl.order_id AS orderId, fl.amount AS amount, fl.amount_freeze AS amountFreeze, fl.repay_account AS repayAccount, fl.repay_fee AS repayFee, fl.lower_interest AS lowerInterest, fl.penalty_amount AS penaltyAmount, fl.default_interest AS defaultInterest, fl.create_time AS createTimeView FROM ht_bank_repay_org_freeze_log fl LEFT JOIN ht_hjh_inst_config ic ON fl.inst_code = ic.inst_code
ORDER BY fl.id desc
LIMIT #{limitStart} , #{limitEnd}
