mapper.sql:
where 1=1

    AND log.borrow_nid = #{borrowNid}


    AND log.batch_no = #{batchNo}


    AND cast(log.`status` AS CHAR) = #{status}




    AND log.create_time >= STR_TO_DATE(#{submitTimeStartSrch},'%Y-%m-%d %H:%i:%s')




    AND log.create_time <= STR_TO_DATE(CONCAT(#{submitTimeEndSrch},' 23:59:59'),'%Y-%m-%d %H:%i:%s')




    AND bi.inst_code = #{instCodeSrch}

mapper.select:
SELECT log.id, log.borrow_nid borrow_nid, hic.inst_name, log.batch_no batch_no, log.user_id user_id, log.user_name user_name, log.period_now period_now, log.borrow_period borrow_period, log.borrow_account borrow_account, log.batch_amount batch_amount, log.batch_service_fee batch_service_fee, log.batch_counts batch_counts, log.suc_amount suc_amount, log.suc_counts suc_counts, log.fail_amount fail_amount, log.fail_counts fail_counts, log.`status`, log.seq_no, log.bank_seq_no, DATE_FORMAT( log.create_time, '%Y-%m-%d %H:%i:%s' ) AS create_time, DATE_FORMAT( log.update_time, '%Y-%m-%d %H:%i:%s' ) AS update_time, log.is_repay_org_flag is_repay_org_flag, log. DATA, b.increase_interest_flag, hba.extra_yield_status, hba.extra_yield_repay_status FROM ht_borrow_apicron hba JOIN ht_borrow_apicron_log log ON hba.bank_seq_no = log.bank_seq_no LEFT JOIN ht_borrow b ON hba.borrow_nid = b.borrow_nid LEFT JOIN ht_borrow_info bi ON hba.borrow_nid = bi.borrow_nid LEFT JOIN ht_hjh_inst_config hic ON hic.inst_code = bi.inst_code
ORDER BY log.id DESC
LIMIT #{limitStart} , #{limitEnd}
