mapper.select:
SELECT sum(counts) FROM ( SELECT count(1) AS counts FROM ht_borrow_tender br WHERE br.user_id = #{userId,jdbcType=INTEGER} AND br.accede_order_id is NULL AND br.status = 1 UNION ALL SELECT count(1) AS counts FROM ht_credit_tender hct WHERE hct.user_id = #{userId,jdbcType=INTEGER}
UNION ALL SELECT count(1) AS counts FROM ht_hjh_accede hha WHERE hha.user_id = #{userId,jdbcType=INTEGER}
UNION ALL SELECT Count(1) as counts FROM ht_debt_plan_accede hdpa -- 汇添金计划加入明细表 left join ht_debt_plan hdp -- 汇添金计划总表 on hdpa.plan_nid=hdp. debt_plan_nid WHERE hdpa.user_id = #{userId,jdbcType=INTEGER}
UNION ALL SELECT count(1) AS counts FROM ht_product_list hpl -- 理财产品购买明细-汇添利 WHERE hpl.user_id = #{userId,jdbcType=INTEGER} and invest_status = 0 ) tmp
