mapper.select:
select hct.user_name AS userName, hct.credit_user_name AS creditUserName, hct.status AS status, hct.bid_nid AS bidNid, hct.credit_nid AS creditNid, hct.credit_tender_nid AS creditTenderNid, hct.assign_nid AS assignNid, hct.assign_capital AS assignCapital, hct.assign_account AS assignAccount, hct.assign_interest AS assignInterest, hct.assign_price AS assignPrice, hct.assign_pay AS assignPay, hct.assign_repay_account AS assignRepayAccount, (select sum(manage_fee) from ht_credit_repay where assign_nid= hct.assign_nid) AS creditFee, CASE WHEN hct.assign_repay_end_time != 0 THEN FROM_UNIXTIME(hct.assign_repay_end_time, '%Y-%m-%d') ELSE '' END AS assignRepayEndTime, CASE WHEN hct.assign_repay_next_time != 0 THEN FROM_UNIXTIME(hct.assign_repay_next_time, '%Y-%m-%d') ELSE '' END AS assignRepayNextTime, hct.create_time AS addTime FROM ht_credit_tender hct
ORDER BY hct.create_time DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}

select sum(hbt.account) from ht_credit_tender hct


		and hct.user_id in

			#{item}



		AND hct.create_time >= #{dayStart}


		AND hct.create_time <= #{dayEnd}

SELECT hct.assign_id AS id, CONCAT( substring(hu.truename, 1, 1), '**' ) AS userName, -- CASE WHEN hui.vip_id IS NOT NULL THEN 1 ELSE 0 END AS is_vip, SUBSTRING(FORMAT(hct.assign_capital,4),1,LENGTH(FORMAT(hct.assign_capital,4))-2) AS assignCapital, date_format(hct.create_time, '%Y-%m-%d %H:%i:%s') AS assignTime, hct.client AS client FROM ht_credit_tender hct LEFT JOIN ht_r_user hu ON hu.user_id = hct.user_id
AND hct.credit_nid = #{creditNid}
ORDER BY hct.create_time DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}

SELECT SUM(credit_fee) AS creditFee
	FROM ht_credit_tender
  WHERE credit_nid = #{creditNid}

SELECT
CASE WHEN hbc.credit_capital_assigned = 0 THEN 0.00
ELSE
(select  sum(hct.assign_pay)-sum(hct.credit_fee) from ht_credit_tender hct
where 1=1


	AND hct.credit_nid = #{creditNid}

)
END AS assignPay

FROM
ht_borrow_credit hbc


		AND hbc.credit_user_id = #{userId}



		AND hbc.credit_nid = #{creditNid}
