sql.where:
    b.`status` = 1
    AND (habt.auto_record != 1 OR habt.auto_record IS NULL)

		AND b.borrow_nid LIKE CONCAT( #{borrowNidSrch}, '%')


		AND b.borrow_period=#{borrowPeriod}


		AND ( u.truename LIKE CONCAT( #{usernameSrch}, '%') OR u.username LIKE CONCAT( #{usernameSrch}, '%') )


		AND b.create_time >= STR_TO_DATE(#{timeStartSrch}, '%Y-%m-%d %H:%i:%s')


		AND b.create_time <= STR_TO_DATE(CONCAT(#{timeEndSrch}, ' 23:59:59'),'%Y-%m-%d %H:%i:%s')


		AND b.verify_status = #{verifyStatusSrch}


		AND bi.inst_code = #{instCodeSrch}

mapper.select:
SELECT b.borrow_nid, u.username, bi.`name` AS borrow_name, FORMAT(b.account, 2) AS account, CONCAT(b.borrow_apr, '%') borrow_apr, CASE WHEN borrow_style = 'endday' THEN CONCAT(b.borrow_period, '天') ELSE CONCAT(b.borrow_period, '个月') END borrow_period, bb.borrow_nid AS is_bail, CASE WHEN b.ontime IS NULL OR b.ontime = 0 THEN '' ELSE FROM_UNIXTIME( b.ontime, '%Y-%m-%d %H:%i:%s' ) END ontime, DATE_FORMAT(b.create_time, '%Y-%m-%d %H:%i:%s' ) AS create_time, b.verify_status, b.project_type, hic.inst_name FROM ht_borrow b LEFT JOIN ht_borrow_info bi ON b.borrow_nid = bi.borrow_nid LEFT JOIN ht_borrow_bail bb ON b.borrow_nid = bb.borrow_nid LEFT JOIN ht_r_user u ON b.user_id = u.user_id LEFT JOIN ht_hjh_inst_config hic ON hic.inst_code = bi.inst_code LEFT JOIN ht_hjh_asset_borrowtype habt ON b.project_type = habt.borrow_cd AND bi.inst_code = habt.inst_code AND bi.asset_type = habt.asset_type AND habt.is_open = 1
ORDER BY b.create_time DESC, b.id DESC
LIMIT #{limitStart} , #{limitEnd}
