mapper.select:
SELECT b.id, b.borrow_nid, bi.`name` AS borrow_name, b.borrow_user_name AS username, b.account, CONCAT(b.borrow_apr, '%') borrow_apr, CASE WHEN b.borrow_style = 'endday' THEN CONCAT(b.borrow_period, '天') ELSE CONCAT(b.borrow_period, '个月') END borrow_period, CASE WHEN b.borrow_account_yes > b.account THEN 1 ELSE 0 END AS account_flag, b.borrow_account_yes AS borrow_account_yes, CASE WHEN b.borrow_service IS NULL OR b.borrow_service = '' THEN '' ELSE b.borrow_service END service_scale, CASE WHEN b.borrow_full_time IS NOT NULL AND b.borrow_full_status = 1 THEN FROM_UNIXTIME( b.borrow_full_time, '%Y-%m-%d %H:%i:%s' ) ELSE FROM_UNIXTIME( b.verify_time + (b.borrow_valid_time * 24 * 60 * 60), '%Y-%m-%d %H:%i:%s' ) END AS over_time, b.create_time, b.reverify_status, b.is_engine_used AS isEngineUsed, b.`status` FROM ht_borrow b LEFT JOIN ht_borrow_info bi ON bi.borrow_nid = b.borrow_nid LEFT JOIN ht_hjh_asset_borrowtype habt ON b.project_type = habt.borrow_cd AND bi.inst_code = habt.inst_code AND bi.asset_type = habt.asset_type AND habt.is_open = 1
ORDER BY b.create_time DESC, b.id DESC
LIMIT #{limitStart} , #{limitEnd}

SELECT hbfnc.man_charge_cd, hbfnc.man_charge_rate, hbfnc.return_rate FROM ht_borrow_finman_new_charge hbfnc LEFT JOIN ht_borrow_project_type hbpt ON hbfnc.project_type=hbpt.borrow_class WHERE hbfnc.status = 0 and hbfnc.man_charge_time=#{borrowPeriod}
and hbfnc.man_charge_time_type=#{borrowStyle}
and hbfnc.man_charge_time_type='month'
and hbpt.borrow_cd=#{projectType} ORDER BY hbfnc.create_time LIMIT 1

SELECT
	hbfnc.charge_rate
FROM
	ht_borrow_finman_new_charge hbfnc
LEFT JOIN ht_borrow_project_type hbpt ON hbfnc.project_type=hbpt.borrow_class
WHERE
hbfnc.status = 0
and hbfnc.man_charge_time=#{borrowPeriod}
and hbfnc.inst_code = #{instCode}
and hbfnc.asset_type = #{assetType}

	and hbfnc.man_charge_time_type=#{borrowStyle}


	and hbfnc.man_charge_time_type='month'

and hbpt.borrow_cd = #{projectType}
ORDER BY
hbfnc.create_time
LIMIT 1

