mapper.select:
SELECT bi.`name` AS borrow_name, bt.user_name AS username, bt.invite_user_name AS referrer_name, bt.borrow_nid, bt.user_id AS userid, bt.nid AS nid, CONCAT(b.borrow_apr, '%') borrow_apr, CASE WHEN borrow_style = 'endday' THEN CONCAT(b.borrow_period, '天') ELSE CONCAT(b.borrow_period, '个月') END borrow_period, CASE WHEN b.`status` = 4 THEN 1 WHEN b.`status` = 5 THEN 1 ELSE 0 END resendMessage, bs.`name` AS borrow_style_name, FORMAT(bt.account, 2) AS account, bt.client AS operating_deck, bt.tender_user_attribute AS user_property, bt.invest_type AS investType, DATE_FORMAT( bt.create_time, '%Y-%m-%d %H:%i:%s' ) AS create_time, hic.inst_name, b.plan_nid, IFNULL(FROM_UNIXTIME( IF(b.reverify_time=0,NULL,b.reverify_time), '%Y-%m-%d %H:%i:%s' ),'') AS reverify_time, bpt.borrow_name AS product_type, hta.status AS contractStatus, hta.contract_number AS contract_number, hta.download_url AS download_url, hta.viewpdf_url AS viewpdf_url, hta.img_url AS img_url, CASE WHEN bt.tender_type = 1 THEN '是' ELSE '否' END AS tender_type FROM ht_borrow_tender bt INNER JOIN ht_borrow b ON bt.borrow_nid = b.borrow_nid INNER JOIN ht_borrow_info bi ON bt.borrow_nid = bi.borrow_nid LEFT JOIN ht_borrow_style bs ON bs.nid = b.borrow_style LEFT JOIN ht_hjh_inst_config hic ON hic.inst_code = bi.inst_code LEFT JOIN ht_borrow_project_type bpt ON bpt.borrow_cd = b.project_type LEFT JOIN ht_tender_agreement hta ON hta.tender_nid = bt.nid
ORDER BY bt.create_time DESC, bt.id DESC
LIMIT #{limitStart} , #{limitEnd}

SELECT b.borrow_nid AS borrow_nid, b.user_id AS user_id, b.borrow_user_name AS username, bi.`name` AS borrow_name, bpt.borrow_name AS borrow_project_type_name, CASE WHEN borrow_style = 'endday' THEN CONCAT(b.borrow_period, '天') ELSE CONCAT(b.borrow_period, '个月') END borrow_period, CONCAT(b.borrow_apr, '%') borrow_apr, bs.`name` AS borrow_style_name, bt.nid AS tender_order_num, bt.nid AS freeze_order_num, bt.user_name AS tender_username, bt.user_id AS tender_user_id, u.attribute AS tender_user_attribute_now, CASE WHEN u.attribute = '1' THEN `od6`.`name` ELSE `od3`.`name` END `tender_region_name`, CASE WHEN u.attribute = '1' THEN `od5`.`name` ELSE `od2`.`name` END `tender_branch_name`, CASE WHEN u.attribute = '1' THEN `od4`.`name` ELSE `od`.`name` END `tender_department_name`, '' AS utm_source, spu.username AS referrer_name, spu.user_id AS referrer_user_id, spu.truename AS referrer_true_name, `od6`.`name` AS `referrer_region_name`, `od5`.`name` AS `referrer_branch_name`, `od4`.`name` AS `referrer_department_name`, bt.tender_user_attribute, bt.invite_user_attribute, bt.invite_user_id AS tender_referrer_user_id, bt.invite_user_name AS tender_referrer_username, bt.invite_region_name AS department_level_1_name, bt.invite_branch_name AS department_level_2_name, bt.invite_department_name AS team_name, bt.account AS account, bt.client AS operating_deck, DATE_FORMAT( bt.create_time, '%Y-%m-%d %H:%i:%s' ) AS create_time, bt.invest_type AS investType, b.plan_nid, hta.status AS contractStatus, hta.contract_number AS contractNumber, hta.contract_name AS contractName, hta.templet_id AS templetId, CASE WHEN hta.contract_create_time IS NOT NULL THEN FROM_UNIXTIME(hta.contract_create_time, '%Y-%m-%d %H:%i:%s' ) ELSE '' END AS contractCreateTime, CASE WHEN hta.contract_sign_time IS NOT NULL AND hta.contract_sign_time!=0 THEN FROM_UNIXTIME(hta.contract_sign_time, '%Y-%m-%d %H:%i:%s' ) ELSE '' END AS contractSignTime, CASE WHEN bt.tender_type = 1 THEN '是' ELSE '否' END AS tender_type, if(bt.create_time<'2019-5-31 0:0:0',utm2.utm_source,utm.utm_source) as utmSource1, utm2.utm_source utmSource2 FROM ht_borrow_tender bt INNER JOIN ht_borrow b ON bt.borrow_nid = b.borrow_nid INNER JOIN ht_borrow_info bi ON bt.borrow_nid = bi.borrow_nid LEFT JOIN ht_borrow_project_type bpt ON bpt.borrow_cd = b.project_type LEFT JOIN ht_borrow_style bs ON bs.nid = b.borrow_style LEFT JOIN ht_r_user u ON u.user_id = bt.user_id LEFT JOIN ht_r_user spu ON spu.user_id = u.spreads_user_id LEFT JOIN ht_r_oa_users `ou` ON `ou`.hyd_id = bt.user_id AND ou.user_status IN ('E','Q1','Q11','Q2','Q21') LEFT JOIN ht_r_oa_department `od` ON `od`.`id` = `ou`.`departmentid` AND `od`.id IS NOT NULL LEFT JOIN ht_r_oa_department `od2` ON `od2`.`id` = `od`.`parentid` LEFT JOIN ht_r_oa_department `od3` ON `od3`.`id` = `od2`.`parentid` LEFT JOIN ht_r_oa_users `ou1` ON `ou1`.hyd_id = spu.user_id AND ou1.user_status IN ('E','Q1','Q11','Q2','Q21') LEFT JOIN ht_r_oa_department `od4` ON `od4`.`id` = `ou1`.`departmentid` AND `od4`.id IS NOT NULL LEFT JOIN ht_r_oa_department `od5` ON `od5`.`id` = `od4`.`parentid` LEFT JOIN ht_r_oa_department `od6` ON `od6`.`id` = `od5`.`parentid` LEFT JOIN ht_tender_agreement hta ON hta.tender_nid = bt.nid left join hyjf_user.ht_utm utm on utm.utm_id=bt.tender_user_utm_id left join hyjf_user.ht_utm_reg reg on bt.user_id=reg.user_id left join hyjf_user.ht_utm utm2 on utm2.utm_id=reg.utm_id
ORDER BY bt.create_time DESC, bt.id DESC
LIMIT #{limitStart} , #{limitEnd}
