sql.where:
    b.`status` = 0
	AND (habt.auto_record != 1 OR b.regist_status = 3 OR b.regist_status = 4 OR habt.auto_record IS NULL)

		AND b.borrow_nid LIKE CONCAT('%', #{borrowNidSrch}, '%')


		AND b.borrow_period=#{borrowPeriodSrch}


		AND b.borrow_user_name LIKE CONCAT('%', #{usernameSrch}, '%')


		AND b.project_type = #{projectTypeSrch}


		AND b.borrow_style = #{borrowStyleSrch}


		AND b.create_time >= STR_TO_DATE(#{timeStartSrch}, '%Y-%m-%d %H:%i:%s')


		AND b.create_time <= STR_TO_DATE(CONCAT(#{timeEndSrch}, ' 23:59:59'),'%Y-%m-%d %H:%i:%s')


		AND b.regist_status = #{registStatusSrch}

mapper.select:
SELECT b.borrow_nid, bi.`name` AS borrow_name, b.borrow_user_name AS username, FORMAT(b.account, 2) AS account, CONCAT(b.borrow_apr, '%') borrow_apr, CASE WHEN borrow_style = 'endday' THEN CONCAT(b.borrow_period, '天') ELSE CONCAT(b.borrow_period, '个月') END borrow_period, b.borrow_style, bs.`name` AS borrow_style_name, bpt.borrow_name AS project_type_name, DATE_FORMAT(b.create_time, '%Y-%m-%d %H:%i:%s' ) AS create_time, b.regist_status, b.project_type FROM ht_borrow b LEFT JOIN ht_borrow_info bi ON bi.borrow_nid = b.borrow_nid LEFT JOIN ht_borrow_style bs ON bs.nid = b.borrow_style LEFT JOIN ht_borrow_project_type bpt ON bpt.borrow_cd = CAST(b.project_type AS CHAR) LEFT JOIN ht_hjh_asset_borrowtype habt ON b.project_type = habt.borrow_cd AND bi.inst_code = habt.inst_code AND bi.asset_type = habt.asset_type AND habt.is_open = 1
ORDER BY b.create_time DESC, b.id DESC
LIMIT #{limitStart} , #{limitEnd}
