mapper.select:
SELECT ct.order_id, bt.user_id, ct.coupon_grant_id, cu.coupon_user_code, bt.borrow_nid FROM ht_borrow_tender_cpn bt INNER JOIN ht_coupon_tender ct ON bt.nid = ct.order_id INNER JOIN ht_coupon_user cu ON ct.coupon_grant_id = cu.id INNER JOIN ht_coupon_config cc ON cu.coupon_code = cc.coupon_code WHERE bt.borrow_nid = #{borrowNid}
AND cc.repay_time_config = #{repayTimeConfig}
AND ct.del_flag = 0

mapper.update:
UPDATE ht_coupon_recover r
SET r.current_recover_flg = #{currentRecoverFlg}
WHERE
	r.tender_id = #{tenderNid}

		AND r.recover_period = #{period}
