mapper.select:
select count(1) from( SELECT hcu.id id, hcu.coupon_user_code coupon_user_code, hct.order_id order_id, hct.attribute, hu.username username, hu.user_id user_id, hcc.coupon_code coupon_code, CASE WHEN hcc.coupon_type = 1 THEN '体验金' WHEN hcc.coupon_type = 2 THEN '加息券' WHEN hcc.coupon_type = 3 THEN '代金券' ELSE '加息券' END coupon_typeStr, hcc.coupon_type coupon_type,
CASE hcc.coupon_type WHEN 1 THEN CONCAT( FORMAT(hcc.coupon_quota, 2), '元' ) WHEN 3 THEN CONCAT( FORMAT(hcc.coupon_quota, 2), '元' ) ELSE CONCAT(hcc.coupon_quota, '%') END AS coupon_quota,
hbt.borrow_nid borrow_nid, hbt1.account account, hbt.client AS operating_deck, CONCAT(hb.borrow_apr, '%') borrow_apr, CASE WHEN borrow_style = 'endday' THEN CONCAT(hb.borrow_period, '天') ELSE CONCAT(hb.borrow_period, '个月') END borrow_period, FROM_UNIXTIME( hct.create_time, '%Y-%m-%d %H:%i' ) order_date, hbt.recover_account_interest_wait recover_account_interest_wait, hcr.received_flg received_flg, (SELECT cr.received_flg FROM ht_coupon_recover cr WHERE cr.tender_id = hbt.nid ORDER BY cr.recover_period DESC LIMIT 1) AS received_flg_all FROM ht_coupon_tender hct INNER JOIN ht_borrow_tender_cpn hbt ON hct.order_id = hbt.nid AND hbt.tender_type=1 INNER JOIN ht_coupon_user hcu ON hct.coupon_grant_id = hcu.id INNER JOIN ht_coupon_config hcc ON hcu.coupon_code = hcc.coupon_code INNER JOIN ht_r_user hu ON hbt.user_id = hu.user_id INNER JOIN ht_borrow hb ON hbt.borrow_nid = hb.borrow_nid LEFT JOIN ht_coupon_real_tender hcrt ON hct.order_id=hcrt.coupon_tender_id LEFT JOIN ht_borrow_tender hbt1 ON hcrt.real_tender_id=hbt1.nid INNER JOIN ht_coupon_recover hcr ON hcr.tender_id = hbt.nid
GROUP BY hct.order_id ORDER BY hct.id DESC
LIMIT #{limitStart} , #{limitEnd}
) t

sql.where:


    AND hcu.id = #{couponUserId}



    AND hcu.used_flag = #{userFlag}

AND hcu.del_flag=0

1 = 1


    AND hct.order_id = #{orderId}



    AND hu.username = #{username}



    AND hcc.coupon_code = #{couponCode}



    AND hcc.coupon_type = #{couponType}



    AND hbt.borrow_nid = #{borrowNid}



    AND hbt.client = #{operatingDeck}




    AND hcr.received_flg = #{receivedFlg}



    AND hct.create_time >= CONCAT(#{timeStartSrch},' 00:00:00')


    AND hct.create_time <= CONCAT(#{timeEndSrch},' 23:59:59')



    AND hcu.id = #{couponUserId}



    AND hcu.used_flag = #{userFlag}

AND hcu.del_flag=0

1 = 1


    AND hct.order_id = #{orderId}



    AND hu.username = #{username}



    AND hcc.coupon_code = #{couponCode}



    AND hcc.coupon_type = #{couponType}



    AND hbt.borrow_nid = #{borrowNid}



    AND hbt.client = #{operatingDeck}




    AND hcr.received_flg = #{receivedFlg}



    AND hct.create_time >= #{timeStartSrch}


    AND hct.create_time <= #{timeEndSrch}

mapper.select:
SELECT hu.username username, hui.truename truename, hu.mobile mobile, "" chinapnr_usrcustid, hbt.nid nid, hbi.name borrow_name, hb.borrow_period borrow_period, CONCAT(hb.borrow_apr,'%') borrow_apr, hbt.borrow_nid borrow_nid, hcc.coupon_quota coupon_quota, hcc.coupon_profit_time coupon_profit_time, hcu.coupon_user_code coupon_user_code, date_format(hbt.create_time,'%Y-%m-%d') add_time, date_format(hcr.create_time, '%Y-%m-%d') hcr_add_time, FROM_UNIXTIME(hcr.recover_time,'%Y-%m-%d') recover_time, hcr.recover_account recover_interest, pa.name received_flg_string, hcr.received_flg as received_flg, IFNULL(hcr.transfer_id,'') transfer_id, FROM_UNIXTIME(hcr.recover_yestime,'%Y-%m-%d') recover_yestime, IFNULL(hbten.account,'0.00') recover_capital FROM ht_coupon_tender hct INNER JOIN ht_coupon_user hcu ON hct.coupon_grant_id = hcu.id INNER JOIN ht_coupon_config hcc ON hcu.coupon_code = hcc.coupon_code INNER JOIN ht_borrow_tender_cpn hbt ON hct.order_id = hbt.nid AND hbt.tender_type=1 INNER JOIN ht_borrow hb ON hbt.borrow_nid = hb.borrow_nid INNER JOIN ht_borrow_info hbi ON hbi.borrow_nid = hb.borrow_nid INNER JOIN ht_r_user hu ON hcu.user_id = hu.user_id INNER JOIN hyjf_user.ht_user_info hui ON hui.user_id = hu.user_id INNER JOIN ht_coupon_recover hcr ON hcr.tender_id = hbt.nid INNER JOIN hyjf_config.ht_param_name pa ON pa.name_class = 'COUPON_RECIVE_STATUS' AND pa.name_cd = hcr.received_flg LEFT JOIN ht_coupon_real_tender hcrt ON hct.order_id=hcrt.coupon_tender_id LEFT JOIN ht_borrow_tender hbten ON hcrt.real_tender_id=hbten.nid
ORDER BY hcr.recover_time DESC
LIMIT #{limitStart} , #{limitEnd}

SELECT hcu.id id, hcc.coupon_code coupon_code, CASE WHEN hcr.received_flg = 1 THEN '未回款' WHEN hcr.received_flg = 2 THEN '未领取' WHEN hcr.received_flg = 3 THEN '转账中' WHEN hcr.received_flg = 4 THEN '转账失败' WHEN hcr.received_flg = 5 THEN '已领取' WHEN hcr.received_flg = 6 THEN '已过期' ELSE '' END received_flg_string, CASE WHEN hcu.coupon_source = 1 THEN '手动发放' WHEN hcu.coupon_source = 2 THEN '活动发放' WHEN hcu.coupon_source = 3 THEN 'vip礼包' ELSE '' END coupon_source, hcc.content coupon_content, hcu.coupon_user_code coupon_user_code, hbt.nid nid, hu.username username, hcc.coupon_code coupon_code, hbt.borrow_nid borrow_nid, CONCAT('第',hcr.recover_period, '期') recover_period, hcr.recover_account recover_interest, hcr.transfer_id transfer_id, -- pa.name received_flg, hcr.received_flg received_flg, FROM_UNIXTIME( hcr.recover_time, '%Y-%m-%d' ) recover_time, IFNULL(hbten.account,'0.00') recover_capital, DATE_FORMAT( hbt.create_time, '%Y-%m-%d' ) add_time FROM ht_coupon_tender hct INNER JOIN ht_coupon_user hcu ON hct.coupon_grant_id = hcu.id INNER JOIN ht_coupon_config hcc ON hcu.coupon_code = hcc.coupon_code INNER JOIN ht_borrow_tender_cpn hbt ON hct.order_id = hbt.nid AND hbt.tender_type=1 INNER JOIN ht_r_user hu ON hcu.user_id = hu.user_id INNER JOIN ht_coupon_recover hcr ON hcr.tender_id = hbt.nid -- INNER JOIN ht_param_name pa ON pa.name_class = 'COUPON_RECIVE_STATUS' -- AND pa.name_cd = hcr.received_flg LEFT JOIN ht_coupon_real_tender hcrt ON hct.order_id=hcrt.coupon_tender_id LEFT JOIN ht_borrow_tender hbten ON hcrt.real_tender_id=hbten.nid
ORDER BY hcr.recover_time DESC
LIMIT #{limitStart} , #{limitEnd}
