sql.where:
1=1

    AND hhr.accede_order_id LIKE CONCAT( #{accedeOrderId}, '%')


    AND hhr.plan_nid LIKE CONCAT( #{planNidSrch}, '%')


    AND hhr.user_name LIKE CONCAT( #{userName}, '%')


    AND hha.invite_user_name LIKE CONCAT( #{refereeNameSrch}, '%')


    AND hhr.lock_period = #{debtLockPeriodSrch}


    AND hhr.repay_status = #{repayStatus}


    AND hhr.order_status = #{orderStatusSrch}


    AND hhp.borrow_style = #{borrowStyleSrch}


    AND hhr.repay_should_time >= unix_timestamp(#{repayTimeStart})


    AND hhr.repay_should_time <= unix_timestamp(#{repayTimeEnd})


    AND hhr.repay_actual_time >= unix_timestamp(#{actulRepayTimeStart})


    AND hhr.repay_actual_time <= unix_timestamp(#{actulRepayTimeEnd})

mapper.select:
SELECT hhr.id, hhr.accede_order_id, hhr.plan_nid, hhr.lock_period, hhr.user_id, hhr.user_name, hhr.user_attribute, hhr.accede_account, hhr.repay_interest, hhr.repay_capital, hhr.repay_status, hhr.repay_already, hhr.repay_wait, hhr.repay_should, hhr.repay_actual, hhr.order_status, CASE WHEN hhr.repay_actual_time != 0 THEN FROM_UNIXTIME(hhr.repay_actual_time, '%Y-%m-%d %H:%i:%s' ) ELSE NULL END AS repay_actual_time, CASE hhr.repay_should_time WHEN NULL THEN NULL ELSE FROM_UNIXTIME(hhr.repay_should_time, '%Y-%m-%d %H:%i:%s' ) END AS repay_should_time, -- hhr.repay_actual_time, -- hhr.repay_should_time, hhr.plan_repay_capital, hhr.plan_repay_interest, hhr.repay_total, hhr.plan_wait_captical, hhr.plan_wait_interest, hhr.wait_total, hhr.service_fee, hhr.create_user, hhr.create_time, hhr.update_user, hhr.update_time, hha.invite_user_name AS recommend_name, -- pna.`name` AS recommend_attr, hha.invite_user_name, -- pna_tender.`name` AS invite_user_attributename, hha.invite_user_regionname, hha.invite_user_branchname, hha.invite_user_departmentname, hha.expect_apr AS expectApr, hha.invite_user_name AS inviteUser, hhr.actual_revenue AS actualRevenue, hhr.actual_pay_total AS actualPayTotal, hha.lqd_service_fee AS lqdServiceFee, hha.lqd_service_apr AS lqdServiceApr, hha.invest_service_apr AS investServiceApr, CONCAT(FORMAT(hha.lqd_progress * 100,0),'%') AS lqdProgress, DATE_ADD(FROM_UNIXTIME(hhr.repay_should_time),INTERVAL 3 DAY) AS lastQuitTime, IFNULL(hha.create_time,0) AS joinTime, IFNULL(hha.count_interest_time,0) AS orderLockTime, hhp.plan_name AS planName FROM ht_hjh_repay hhr LEFT JOIN ht_hjh_accede hha ON hha.accede_order_id = hhr.accede_order_id -- LEFT JOIN ht_spreads_users hydsu ON hhr.user_id = hydsu.user_id -- LEFT JOIN ht_r_users hu ON hydsu.spreads_userid = hu.user_id -- LEFT JOIN ht_users_info hui ON hui.user_id = hu.user_id LEFT JOIN ht_hjh_plan hhp ON hhp.plan_nid = hhr.plan_nid -- LEFT JOIN ht_param_name pna ON cast(hhr.user_attribute AS CHAR) = pna.name_cd -- AND pna.name_class = 'USER_PROPERTY' -- LEFT JOIN ht_param_name pna_tender ON cast(hha.invite_user_attribute AS CHAR) = pna_tender.name_cd -- AND pna_tender.name_class = 'USER_PROPERTY'
ORDER BY repay_should_time ASC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}
