sql.where:
bt.tender_type = 1

    AND ha.plan_nid = #{planNid }


    AND  bt.create_time  >=  #{date}


    AND  bt.create_time  <=  #{datee}


    AND bt.accede_order_id = #{accedeOrderIdSrch }


    AND hu.username = #{userNameSrch }


    AND bt.borrow_nid = #{borrowNidSrch }


    AND hb.borrow_period = #{lockPeriodSrch }


    AND bt.invest_type = #{investTypeSrch }


    AND bs.`name` = #{borrowStyleSrch }

mapper.select:
SELECT bt.accede_order_id, hp.plan_nid, hu.username, hu.username as invite, hu.attribute, bt.borrow_nid, concat(hp.expect_apr,'%') as expect_apr, hb.borrow_period, concat( hb.borrow_period, (IF(hb.borrow_style = 'endday','天','个月')) ) AS borrow_period_view, CASE WHEN hb.borrow_style = 'endday' THEN '天' ELSE '个月' END AS is_month, bt.account, bs.`name` AS borrow_style_name, bt.invest_type, CASE WHEN ha.count_interest_time <> 0 AND ha.count_interest_time IS NOT NULL THEN FROM_UNIXTIME( ha.count_interest_time, '%Y-%m-%d %H:%i:%s' ) ELSE '' END AS count_interest_time, CASE WHEN bt.create_time <> 0 AND bt.create_time IS NOT NULL THEN bt.create_time ELSE '' END AS add_time FROM ht_borrow_tender bt LEFT JOIN ht_hjh_accede ha ON bt.accede_order_id = ha.accede_order_id LEFT JOIN ht_hjh_plan hp ON ha.plan_nid = hp.plan_nid LEFT JOIN ht_r_user hu ON hu.user_id = bt.user_id LEFT JOIN ht_borrow hb ON hb.borrow_nid = bt.borrow_nid LEFT JOIN ht_borrow_style bs ON bs.nid = hb.borrow_style
ORDER BY bt.create_time DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}
