mapper.select:
select count(*) from(
	SELECT user_id FROM ht_borrow_tender where create_time <=#{date}
	UNION
	SELECT user_id FROM ht_credit_tender where create_time <=#{date}
	UNION
	SELECT user_id  FROM ht_borrow_tender_cpn WHERE create_time <=#{date}
	UNION
	SELECT user_id  FROM ht_hjh_accede  WHERE create_time <=#{date}
) as tmp
