mapper.select:
SELECT api.id, b.borrow_nid, info.`name` AS borrow_name, b.borrow_period, b.borrow_style, b.account, ( SELECT IFNULL(sum(c.commission), 0.00) AS commission FROM ht_tender_commission c WHERE c.borrow_nid = api.borrow_nid ) AS commission, FROM_UNIXTIME( b.recover_last_time, '%Y-%m-%d %H:%i%:%s' ) AS recover_last_time, api.web_status, CASE b.borrow_style WHEN 'endday' THEN CONCAT(b.borrow_period, '天') ELSE CONCAT(b.borrow_period, '个月') END AS rzqx FROM (SELECT * FROM ht_borrow_apicron a WHERE a.api_type = 0 ORDER BY a.id DESC) api JOIN ht_borrow b ON api.borrow_nid = b.borrow_nid AND (b.`status` = 4 OR b.`status` = 5) AND b.plan_nid IS NULL JOIN ht_borrow_info info ON b.borrow_nid = info.borrow_nid
ORDER BY api.web_status,b.recover_last_time DESC
LIMIT #{limitStart} , #{limitEnd}

sql.where:
1=1

    AND b.borrow_nid = #{borrowNid}


    AND info.`name` LIKE CONCAT( #{borrowName}, '%')


    AND FROM_UNIXTIME( b.recover_last_time, '%Y-%m-%d' ) >= #{recoverLastTimeStart}


    AND FROM_UNIXTIME( b.recover_last_time, '%Y-%m-%d' ) <= #{recoverLastTimeEnd}

mapper.sql:
WHERE ( ( 1 = ( SELECT reward_send FROM ht_push_money WHERE project_type = 1 AND type = '线上员工' ) AND EXISTS ( SELECT t.id FROM ht_borrow_tender t WHERE t.borrow_nid = b.borrow_nid AND ( t.tender_user_attribute = 3 OR t.invite_user_attribute = 3 ) ) ) OR (api.web_status = 1) )
AND b.borrow_nid = #{borrowNid}
AND info.`name` LIKE CONCAT( #{borrowName}, '%')
AND FROM_UNIXTIME( b.recover_last_time, '%Y-%m-%d' ) >= #{recoverLastTimeStart}
AND FROM_UNIXTIME( b.recover_last_time, '%Y-%m-%d' ) <= #{recoverLastTimeEnd}

sql.if:
AND tc.department_id in

    #{dep}

AND tc.department_name is not null

mapper.select:
SELECT tc.id, tc.borrow_nid, tc.ordid, tc.account_id, b.borrow_period, b.borrow_style, hhp.lock_period, hhp.borrow_style AS borrow_style_hjh, hhp.is_month AS isMonth, tc.`status`, tc.tender_user_id, hhp.borrow_style, IFNULL(hhp.expect_apr, 0.00) AS expect_apr, IFNULL(tc.account_tender, 0.00) AS account_tender, IFNULL(tc.commission, 0.00) AS commission, CASE WHEN hha.count_interest_time > 0 THEN FROM_UNIXTIME( hha.count_interest_time, '%Y-%m-%d %H:%i:%s' ) ELSE '' END AS count_interest_time, CASE WHEN hha.count_interest_time > 0 THEN FROM_UNIXTIME( hha.create_time, '%Y-%m-%d %H:%i:%s' ) ELSE '' END AS create_time, FROM_UNIXTIME(tc.tender_time,'%Y-%m-%d %H:%i%:%s') as tender_time, FROM_UNIXTIME(tc.send_time,'%Y-%m-%d %H:%i:%S') AS send_time, case b.borrow_style when 'endday' then CONCAT(b.borrow_period,'天') else CONCAT(b.borrow_period,'个月') end as rzqx, case tc.`status` when 1 then '已发放' else '未发放' end as status_name, t.invite_region_name as region_name, t.invite_branch_name as branch_name, t.invite_department_name as department_name, u.username, u2.username AS username_tender, u.attribute, u2.attribute AS attribute_tender, u.truename FROM ht_tender_commission tc LEFT JOIN ht_borrow_tender t ON tc.tender_id= t.id LEFT JOIN ht_r_user u ON u.user_id= tc.user_id LEFT JOIN ht_borrow b ON b.borrow_nid= tc.borrow_nid LEFT JOIN ht_hjh_plan hhp ON hhp.plan_nid = tc.borrow_nid LEFT JOIN ht_r_user u2 ON u2.user_id= tc.tender_user_id LEFT JOIN ht_hjh_accede hha ON hha.accede_order_id = tc.ordid WHERE 1=1 AND tc.commission > 0
ORDER BY tc.status ASC, tc.tender_time DESC
LIMIT #{limitStart} , #{limitEnd}

SELECT u.user_id AS userId, u.username AS userName, u.truename AS trueName,u.mobile,u.attribute, od3.`name` as regionName, od2.`name` as branchName, od.`name` as departmentName FROM ht_r_user u LEFT JOIN ht_r_oa_users ou ON u.user_id= ou.hyd_id AND ou.user_status IN ('E', 'Q1', 'Q11', 'Q2', 'Q21') LEFT JOIN ht_r_oa_department od ON od.id = ou.departmentid LEFT JOIN ht_r_oa_department od2 ON od2.id = od.parentid LEFT JOIN ht_r_oa_department od3 on od3.id = od2.parentid WHERE 1=1
AND u.user_id= #{userId}
group by u.user_id
