SELECT t1.nid, t1.user_id userId, CASE WHEN t4.borrow_style = 'endday' THEN FORMAT((t1.account * t4.borrow_period/365)*0.07,2) ELSE FORMAT((t1.account * t4.borrow_period/12)*0.07,2) END money, FROM_UNIXTIME(t2.reg_time,'%Y-%m-%d %H:%i') reg_time,t2.username username,t3.truename truename,INSERT(t2.mobile, 4, 4, '****') mobile,'散标' type,t1.borrow_nid AS plannid,i.name,t1.account, CASE WHEN t4.borrow_style = 'endday' THEN CONCAT(t4.borrow_period, '天') ELSE CONCAT(t4.borrow_period, '个月') END borrow_period, CASE WHEN t4.borrow_style = 'endday' THEN FORMAT((t1.account * t4.borrow_period/365),2) ELSE FORMAT((t1.account * t4.borrow_period/12),2) END yearAccount, t5.truename reffername,INSERT(t6.mobile, 4, 4, '****') reffermobile,t1.create_time addtimes FROM ht_borrow_tender t1 LEFT JOIN hyjf_user.ht_user t2 ON t1.user_id = t2.user_id LEFT JOIN hyjf_user.ht_user_info t3 ON t2.user_id = t3.user_id LEFT JOIN ht_borrow t4 ON t1.borrow_nid = t4.borrow_nid LEFT JOIN ht_borrow_info i on i.borrow_nid=t4.borrow_nid LEFT JOIN hyjf_user.ht_spreads_user t7 ON t7.user_id=t2.user_id LEFT JOIN hyjf_user.ht_user t6 ON t6.user_id = t7.spreads_user_id LEFT JOIN hyjf_user.ht_user_info t5 ON t7.spreads_user_id = t5.user_id LEFT JOIN hyjf_user.ht_utm_reg r on t2.user_id=r.user_id LEFT JOIN hyjf_user.ht_utm u on u.utm_id=r.utm_id WHERE u.utm_source like '千乐%' and t1.accede_order_id is null
and t2.create_time>= #{regTimeStart} and t2.create_time <= #{regTimeEnd}
AND t1.create_time >= #{addTimeStart} AND t1.create_time <= #{addTimeEnd}
AND t3.truename=#{truename}
AND t2.username=#{username}
AND t5.truename=#{reffername}
union SELECT t1.accede_order_id AS nid, t1.user_id userId, CASE WHEN t4.is_month = 1 THEN FORMAT((t1.accede_account * t4.lock_period/12)*0.07,2) ELSE FORMAT((t1.accede_account * t4.lock_period/365)*0.07,2) END money, FROM_UNIXTIME(t2.reg_time,'%Y-%m-%d %H:%i') reg_time,t2.username username,t3.truename truename,INSERT(t2.mobile, 4, 4, '****') mobile,'服务' type,t1.plan_nid AS plannid,t4.plan_name as name,t1.accede_account account, CASE WHEN t4.is_month = 1 THEN CONCAT(t4.lock_period, '个月') ELSE CONCAT(t4.lock_period, '天') END borrow_period, CASE WHEN t4.is_month = 1 THEN FORMAT((t1.accede_account * t4.lock_period/12),2) ELSE FORMAT((t1.accede_account * t4.lock_period/365),2) END yearAccount, t5.truename reffername,INSERT(t6.mobile, 4, 4, '****') reffermobile,t1.create_time addtimes FROM ht_hjh_accede t1 LEFT JOIN hyjf_user.ht_user t2 ON t1.user_id = t2.user_id LEFT JOIN hyjf_user.ht_user_info t3 ON t2.user_id = t3.user_id LEFT JOIN ht_hjh_plan t4 ON t1.plan_nid = t4.plan_nid LEFT JOIN hyjf_user.ht_spreads_user t7 ON t7.user_id=t2.user_id LEFT JOIN hyjf_user.ht_user t6 ON t6.user_id = t7.spreads_user_id LEFT JOIN hyjf_user.ht_user_info t5 ON t7.spreads_user_id = t5.user_id LEFT JOIN hyjf_user.ht_utm_reg r on t2.user_id=r.user_id LEFT JOIN hyjf_user.ht_utm u on u.utm_id=r.utm_id WHERE u.utm_source like '千乐%'
and t2.create_time>= #{regTimeStart} and t2.create_time <= #{regTimeEnd}
AND t1.create_time >= #{addTimeStart} AND t1.create_time <= #{addTimeEnd}
AND t3.truename=#{truename}
AND t2.username=#{username}
AND t5.truename=#{reffername}
order by addtimes DESC
limit #{limitStart} , #{limitEnd}

