mapper.select:
SELECT ui.sex as sex,count(1) as count FROM (SELECT br.user_id FROM ht_borrow_tender br INNER JOIN ht_borrow b ON b.borrow_nid = br.borrow_nid WHERE b.recover_last_time <= unix_timestamp(#{0}) UNION SELECT user_id FROM ht_credit_tender WHERE create_time <= #{0}
UNION SELECT hdpa.user_id FROM ht_debt_plan_accede hdpa left join ht_debt_plan hdp on hdpa.plan_nid=hdp. debt_plan_nid WHERE hdp.plan_lock_time <= unix_timestamp(#{0})
UNION SELECT user_id FROM ht_product_list WHERE invest_time <= unix_timestamp(#{0}) And invest_status = 0 ) t LEFT JOIN ht_user u on t.user_id=u.user_id LEFT JOIN ht_user_info ui ON u.user_id = ui.user_id where ui.sex=2 or ui.sex=1 group by ui.sex;

SELECT count(t.user_id) FROM (SELECT br .user_id FROM ht_borrow_tender br INNER JOIN ht_borrow b ON b.borrow_nid = br.borrow_nid WHERE b.recover_last_time <= unix_timestamp(#{date}) UNION SELECT user_id FROM ht_credit_tender WHERE create_time <= #{date}
UNION SELECT hdpa.user_id FROM ht_debt_plan_accede hdpa left join ht_debt_plan hdp on hdpa.plan_nid=hdp. debt_plan_nid WHERE hdp.plan_lock_time <= unix_timestamp(#{date})
UNION SELECT user_id FROM ht_product_list WHERE invest_time <= unix_timestamp(#{date}) And invest_status = 0 ) t LEFT JOIN ht_user u on t.user_id=u.user_id LEFT JOIN ht_user_info ui ON u.user_id = ui.user_id WHERE TIMESTAMPDIFF(YEAR, ui.birthday, now()) <#{endAge} and TIMESTAMPDIFF(YEAR, ui.birthday, now()) >=#{firstAge}

select count(*) from(
	SELECT user_id FROM ht_borrow_tender where create_time <=#{date}
	UNION
	SELECT user_id FROM ht_credit_tender where create_time <=#{date}
	UNION
	SELECT user_id  FROM ht_borrow_tender_cpn WHERE create_time  <=#{date}
	UNION
	SELECT user_id  FROM ht_hjh_accede  WHERE create_time  <=#{date}
) as tmp
