mapper.select:
SELECT b.borrow_nid AS borrowNid, b.borrow_period AS borrowPeriod, bs.name AS repayStyleName, hiil.user_name AS investUserName, hiil.user_id AS investUserId, hiil.invest_id AS investId, b.borrow_apr AS borrowApr, b.borrow_style AS borrowStyle, b.borrow_extra_yield AS borrowExtraYield, hiii.account AS repayCapital, hiii.repay_interest AS repayInterest, hiil.repay_time AS repayTime, hiil.repay_status AS repayStatus, hiil.repay_action_time as repayActionTime, hiil.repay_interest_yes as repayInterestYes FROM ht_borrow AS b INNER JOIN ht_increase_interest_loan AS hiil ON hiil.borrow_nid = b.borrow_nid LEFT JOIN ht_borrow_style AS bs on b.borrow_style= bs.nid LEFT JOIN ht_increase_interest_invest AS hiii ON hiil.invest_id= hiii.id
ORDER BY hiil.repay_time ASC,b.id ASC
LIMIT #{limitStart} , #{limitEnd}

SELECT COUNT(1) FROM ht_borrow AS b INNER JOIN ht_increase_interest_loan_detail AS hiild on b.borrow_nid = hiild.borrow_nid LEFT join ht_increase_interest_invest AS hiii on hiild.invest_id= hiii.id LEFT JOIN ht_borrow_style AS bs on b.borrow_style=bs.nid where 1=1
AND hiild.borrow_nid LIKE CONCAT( #{borrowNidSrch}, '%')
AND hiild.user_name LIKE CONCAT( #{userNameSrch},'%')
AND hiild.invest_id = #{investIdSrch}
AND hiild.repay_period = #{repayPeriodSrch}
AND FROM_UNIXTIME( hiild.repay_time, '%Y-%m-%d' ) >= #{timeStartSrch}
AND FROM_UNIXTIME( hiild.repay_time, '%Y-%m-%d' ) <= #{timeEndSrch}

SELECT b.borrow_nid AS borrowNid, b.borrow_period AS borrowPeriod, bs.name AS repayStyleName, hiild.user_name AS investUserName, hiild.user_id AS investUserId, b.borrow_apr AS borrowApr, b.borrow_style AS borrowStyle, info.borrow_extra_yield AS borrowExtraYield, hiii.account AS repayCapital, hiild.loan_interest AS repayInterest, hiild.repay_time AS repayTime, hiild.repay_status AS repayStatus, hiild.repay_period AS repayPeriod, hiild.repay_action_time AS repayActionTime, hiild.repay_interest_yes as repayInterestYes FROM ht_borrow AS b INNER JOIN ht_increase_interest_loan_detail AS hiild on b.borrow_nid = hiild.borrow_nid LEFT JOIN ht_borrow_info AS info ON b.borrow_nid = info.borrow_nid LEFT join ht_increase_interest_invest AS hiii on hiild.invest_id= hiii.id LEFT JOIN ht_borrow_style AS bs on b.borrow_style=bs.nid WHERE 1=1
AND hiild.borrow_nid LIKE CONCAT( #{borrowNidSrch}, '%')
AND hiild.user_name LIKE CONCAT( #{userNameSrch},'%')
AND hiild.invest_id = #{investIdSrch}
AND hiild.repay_period = #{repayPeriodSrch}
AND FROM_UNIXTIME( hiild.repay_time, '%Y-%m-%d' ) >= #{timeStartSrch}
AND FROM_UNIXTIME( hiild.repay_time, '%Y-%m-%d' ) <= #{timeEndSrch}
ORDER BY hiild.repay_time ASC,b.id ASC
LIMIT #{limitStart} , #{limitEnd}

SELECT SUM(hiii.account) AS sumRepayCapital, SUM(hiild.loan_interest) AS sumLoanInterest FROM ht_borrow AS b INNER JOIN ht_increase_interest_loan_detail AS hiild on b.borrow_nid = hiild.borrow_nid LEFT join ht_increase_interest_invest AS hiii on hiild.invest_id= hiii.id LEFT JOIN ht_borrow_style AS bs on b.borrow_style=bs.nid WHERE 1=1
AND hiild.borrow_nid LIKE CONCAT( #{borrowNidSrch}, '%')
AND hiild.user_name LIKE CONCAT( #{userNameSrch},'%')
AND hiild.invest_id = #{investIdSrch}
AND hiild.repay_period = #{repayPeriodSrch}
AND FROM_UNIXTIME( hiild.repay_time, '%Y-%m-%d' ) >= #{timeStartSrch}
AND FROM_UNIXTIME( hiild.repay_time, '%Y-%m-%d' ) <= #{timeEndSrch}
