sql.where:
temp.invest_status = 0

	AND FROM_UNIXTIME( temp.invest_time, '%Y-%m-%d' ) >= #{timeStartSrch}


	AND FROM_UNIXTIME( temp.invest_time, '%Y-%m-%d' ) <= #{timeEndSrch}

mapper.select:
SELECT temp.reg_esb AS client, COUNT(temp.user_id) AS regist_number FROM ht_user temp


		AND FROM_UNIXTIME( temp.reg_time, '%Y-%m-%d' ) >= #{timeStartSrch}


		AND FROM_UNIXTIME( temp.reg_time, '%Y-%m-%d' ) <= #{timeEndSrch}


GROUP BY temp.reg_esb
