sql.where:
      hydua.`del_flag` = 0
      
        AND hydua.user_name LIKE CONCAT( #{userName}, '%')
      
      
        AND hydur.username LIKE CONCAT( #{recommendName}, '%')
      
      
        AND hydua.auto_inves_status = #{autoInvesStatus,jdbcType=INTEGER}
      
      
        AND hydua.auto_credit_status = #{autoCreditStatus,jdbcType=INTEGER}
      

      
        AND hydua.inves_cancel_time IS NOT NULL AND hydua.auto_inves_status = 0
      
      
        AND hydua.credit_cancel_time IS NOT NULL AND hydua.auto_credit_status = 0
      

      
        
        AND hydua.auto_create_time >= UNIX_TIMESTAMP(CONCAT(#{invesAddTimeStart}))
      
      
        
        AND hydua.auto_create_time <= UNIX_TIMESTAMP(CONCAT(#{invesAddTimeEnd},' 23:59:59'))
      
      
        
        AND hydua.auto_bid_end_time >= #{investEndTimeStart}
      
      
        
        AND hydua.auto_bid_end_time <= #{investEndTimeEnd}

mapper.select:
SELECT hydua.user_id AS user_id, hydua.user_name AS user_name, hydur.username AS recommend_name, hydur1.mobile AS mobile, CASE WHEN hydua.auto_inves_status = 1 THEN '已授权' ELSE (CASE WHEN hydua.inves_cancel_time IS NOT NULL THEN '已解约' ELSE '未授权' END) END AS auto_inves_status, CASE WHEN hydua.auto_credit_status = 1 THEN '已授权' ELSE (CASE WHEN hydua.credit_cancel_time IS NOT NULL THEN '已解约' ELSE '未授权' END) END AS auto_credit_status, FROM_UNIXTIME( hydua.auto_create_time, '%Y-%m-%d %H:%i:%s' ) AS auto_create_time, hydua.auto_order_id AS auto_order_id, DATE_FORMAT(STR_TO_DATE(hydua.auto_bid_end_time,'%Y%m%d'),'%Y-%m-%d') as auto_bid_end_time, DATE_FORMAT(STR_TO_DATE(hydua.auto_credit_end_time,'%Y%m%d'),'%Y-%m-%d') as auto_credit_end_time, hydua.inves_max_amt inves_max_amt, hydua.credit_max_amt, hydua.auto_credit_order_id AS auto_credit_order_id FROM ht_hjh_user_auth hydua LEFT JOIN ht_spreads_user hydsu ON hydsu.user_id = hydua.user_id LEFT JOIN ht_user hydur ON hydur.user_id = hydsu.spreads_user_id LEFT JOIN ht_user hydur1 ON hydur1.user_id= hydua.user_id
ORDER BY hydua.auto_create_time DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}

SELECT hydual.order_id AS order_id, hydual.user_name AS user_name, CASE WHEN hydual.order_status = 0 THEN '初始' WHEN hydual.order_status = 1 THEN '成功' ELSE '失败' END AS order_status, hydual.auth_type AS auth_type, hydual.operate_esb AS operate_esb, FROM_UNIXTIME( hydual.auth_create_time, '%Y-%m-%d %H:%i:%s' ) AS auth_create_time FROM ht_hjh_user_auth_log hydual
ORDER BY hydual.create_time DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}

mapper.update:
update ht_hjh_user_auth

  auto_payment_end_time = #{1,jdbcType=VARCHAR},
  auto_payment_status = #{2,jdbcType=INTEGER}

where user_id  = #{0,jdbcType=INTEGER}

mapper.select:
SELECT hydual.user_id, hydual.user_name, CASE WHEN hydual.auto_repay_status = 0 and (hydual.repay_cancel_time is not null or hydual.repay_cancel_time <>'' ) then 2 WHEN hydual.auto_repay_status = 0 and (hydual.repay_cancel_time is null or hydual.repay_cancel_time='') then 0 WHEN (hydual.auto_repay_status = 1) THEN 1 END AS auto_repay_status, CASE WHEN hydual.auto_repay_status = 0 THEN '' ELSE DATE_FORMAT( STR_TO_DATE( hydual.auto_repay_end_time, '%Y%m%d' ), '%Y-%m-%d' ) END AS auto_repay_end_time, CASE WHEN hydual.auto_repay_status = 0 THEN '' ELSE FROM_UNIXTIME( hydual.auto_repay_time, '%Y-%m-%d' )END AS auto_repay_time, bank.account, hydual.repay_max_amt FROM ht_hjh_user_auth hydual LEFT JOIN ht_bank_open_account bank ON bank.user_id = hydual.user_id
ORDER BY bank.create_time DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}

mapper.update:
update ht_hjh_user_auth

  auto_repay_end_time = #{1,jdbcType=VARCHAR},
  auto_repay_status = #{2,jdbcType=INTEGER}

where user_id  = #{0,jdbcType=INTEGER}
