mapper.select:
SELECT distinct u.hyd_id as user_id, u.hyd_name as user_name, d3.id as region_id, d3.name as region_name, d2.id as branch_id, d2.name as branch_name, d1.id as department_id, d1.name as department_name FROM ht_r_oa_users u LEFT JOIN ht_r_oa_department d1 ON d1.id= u.departmentid LEFT JOIN ht_r_oa_department d2 ON d2.id= d1.parentid LEFT JOIN ht_r_oa_department d3 ON d3.id= d2.parentid WHERE u.user_status !='Q' AND u.user_status !='E3' AND u.user_status !='E11'
AND u.hyd_id = #{userId}
AND 0=1
