SELECT SUM(access_number) AS access_number FROM ( SELECT a.utm_source, a.utm_id, a.source_id, ( SELECT COUNT(visit.id) FROM ( SELECT * FROM hyjf_user.ht_utm_visit visit
AND visit.create_time >= #{dayStart}
AND visit.create_time <= #{dayEnd}
) visit where visit.utm_id = a.utm_id ) AS access_number FROM ( SELECT utm.utm_source, utm.utm_id, utm.source_id FROM hyjf_user.ht_utm utm
WHERE utm.source_id= #{sourceId}
) a ) b GROUP BY b.source_id

SELECT SUM(regist_number) AS regist_number FROM ( SELECT a.utm_source, a.utm_id, a.source_id, ( ( SELECT COUNT(reg.id) regid FROM ( SELECT reg.id, reg.utm_id FROM hyjf_user.ht_utm_reg reg
AND reg.create_time >= #{dayStart}
AND reg.create_time <= #{dayEnd}
) reg where reg.utm_id = a.utm_id ) + ( SELECT COUNT(reg.id) regid FROM ( SELECT * FROM hyjf_user.ht_user_plat reg
reg.pid = '26'
AND reg.create_time >= #{dayStart}
AND reg.create_time <= #{dayEnd}
) reg INNER JOIN hyjf_user.ht_account_chinapnr nr ON nr.user_id = reg.user_id WHERE a.utm_source = '投之家' ) ) AS regist_number FROM ( SELECT DISTINCT utm.utm_source, utm.utm_id, utm.source_id FROM hyjf_user.ht_utm utm
WHERE utm.source_id= #{sourceId}
) a ) b GROUP BY b.source_id

SELECT SUM(account_number) AS account_number FROM ( SELECT a.utm_source, a.utm_id, a.source_id, ( SELECT COUNT(nr.user_id) FROM hyjf_user.ht_utm_reg reg INNER JOIN ( SELECT user_id FROM hyjf_user.ht_account_chinapnr nr
AND nr.create_time >= #{dayStart}
AND nr.create_time <= #{dayEnd}
) nr ON nr.user_id = reg.user_id WHERE reg.utm_id = a.utm_id ) AS account_number FROM ( SELECT DISTINCT utm.utm_source, utm.utm_id, utm.source_id FROM hyjf_user.ht_utm utm
WHERE utm.source_id= #{sourceId}
) a ) b GROUP BY b.source_id

SELECT SUM(tender_number) AS tender_number FROM ( SELECT a.utm_source, a.utm_id, a.source_id, ( SELECT COUNT(DISTINCT c.user_id) FROM ( SELECT reg.utm_id, reg.user_id FROM hyjf_user.ht_utm_reg reg INNER JOIN ( SELECT * FROM hyjf_trade.ht_borrow_tender tender
AND tender.create_time >= #{dayStart}
AND tender.create_time <= #{dayEnd}
) tender ON tender.user_id = reg.user_id UNION ALL SELECT reg.utm_id, reg.user_id FROM hyjf_user.ht_utm_reg reg INNER JOIN ( SELECT * FROM hyjf_trade.ht_product_list list
list.invest_status = 0
AND list.invest_time >= UNIX_TIMESTAMP(#{dayStart})
AND list.invest_time <= UNIX_TIMESTAMP(#{dayEnd})
) list ON list.user_id = reg.user_id UNION ALL SELECT reg.utm_id, reg.user_id FROM hyjf_user.ht_utm_reg reg INNER JOIN ( select pa.user_id from hyjf_trade.ht_debt_plan_accede pa
AND pa.create_time >= #{dayStart}
AND pa.create_time <= #{dayEnd}
) pa ON pa.user_id = reg.user_id UNION ALL SELECT reg.utm_id, reg.user_id FROM hyjf_user.ht_utm_reg reg INNER JOIN ( SELECT hct.user_id FROM hyjf_trade.ht_credit_tender hct
AND hct.create_time >= #{dayStart}
AND hct.create_time <= #{dayEnd}
) hct ON hct.user_id = reg.user_id
) c WHERE c.utm_id = a.utm_id ) AS tender_number FROM ( SELECT DISTINCT utm.utm_source, utm.utm_id, utm.source_id FROM hyjf_user.ht_utm utm
WHERE utm.source_id= #{sourceId}
) a ) b GROUP BY b.source_id

SELECT SUM(recharge_price) AS recharge_price FROM ( SELECT a.utm_source, a.utm_id, a.source_id, ( SELECT CASE WHEN SUM(recharge.money) IS NULL THEN 0 ELSE SUM(recharge.money) END money FROM hyjf_user.ht_utm_reg reg INNER JOIN ( SELECT * FROM hyjf_trade.ht_account_recharge recharge
recharge.`status` = 1
AND recharge.create_time >= #{dayStart}
AND recharge.create_time <= #{dayEnd}
) recharge ON recharge.user_id = reg.user_id where reg.utm_id = a.utm_id ) AS recharge_price FROM ( SELECT DISTINCT utm.utm_source, utm.utm_id, utm.source_id FROM hyjf_user.ht_utm utm
WHERE utm.source_id= #{sourceId}
) a ) b GROUP BY b.source_id

SELECT CASE WHEN SUM(b.account) IS NULL THEN 0 ELSE SUM(b.account) END AS hzt_tender_price FROM ( SELECT tender.account, a.source_id, a.utm_source, reg.user_id FROM ( SELECT DISTINCT utm.utm_source, utm.utm_id, utm.source_id FROM hyjf_user.ht_utm utm
WHERE utm.source_id= #{sourceId}
) a INNER JOIN ( SELECT reg.user_id, reg.utm_id FROM hyjf_user.ht_utm_reg reg ) reg ON a.utm_id = reg.utm_id INNER JOIN ( SELECT tender.account, tender.user_id, tender.borrow_nid FROM hyjf_trade.ht_borrow_tender tender
AND tender.create_time >= #{dayStart}
AND tender.create_time <= #{dayEnd}
) tender ON tender.user_id = reg.user_id INNER JOIN ( SELECT borrow.borrow_nid FROM hyjf_trade.ht_borrow borrow WHERE borrow.project_type <> 8 AND borrow.project_type <> 13 ) borrow ON borrow.borrow_nid = tender.borrow_nid ) b GROUP BY b.source_id

SELECT CASE WHEN SUM(b.account) IS NULL THEN 0 ELSE SUM(b.account) END AS hxf_tender_price FROM ( SELECT tender.account, a.source_id, a.utm_source, reg.user_id FROM ( SELECT DISTINCT utm.utm_source, utm.utm_id, utm.source_id FROM hyjf_user.ht_utm utm
WHERE utm.source_id= #{sourceId}
) a INNER JOIN ( SELECT reg.user_id, reg.utm_id FROM hyjf_user.ht_utm_reg reg ) reg ON a.utm_id = reg.utm_id INNER JOIN ( SELECT tender.account, tender.user_id, tender.borrow_nid FROM hyjf_trade.ht_borrow_tender tender
AND tender.create_time >= #{dayStart}
AND tender.create_time <= #{dayEnd}
) tender ON tender.user_id = reg.user_id INNER JOIN ( SELECT borrow.borrow_nid FROM hyjf_trade.ht_borrow borrow WHERE borrow.project_type = 8 ) borrow ON borrow.borrow_nid = tender.borrow_nid ) b GROUP BY b.source_id

SELECT CASE WHEN SUM(b.account) IS NULL THEN 0 ELSE SUM(b.account) END AS hxf_tender_price FROM ( SELECT list.account, a.source_id, a.utm_source, reg.user_id FROM ( SELECT DISTINCT utm.utm_source, utm.utm_id, utm.source_id FROM hyjf_user.ht_utm utm
WHERE utm.source_id= #{sourceId}
) a INNER JOIN ( SELECT reg.user_id, reg.utm_id FROM hyjf_user.ht_utm_reg reg ) reg ON a.utm_id = reg.utm_id INNER JOIN ( SELECT list.user_id, list.amount AS account FROM hyjf_trade.ht_product_list list
list.invest_status = 0
AND FROM_UNIXTIME( list.invest_time, '%Y-%m-%d %H:%i:%s' ) >= #{dayStart}
AND FROM_UNIXTIME( list.invest_time, '%Y-%m-%d %H:%i:%s' ) <= #{dayEnd}
) list ON list.user_id = reg.user_id ) b GROUP BY b.source_id

SELECT CASE WHEN SUM(b.account) IS NULL THEN 0 ELSE SUM(b.account) END AS htj_tender_price FROM ( SELECT hdpa.account, a.source_id, a.utm_source, reg.user_id FROM ( SELECT DISTINCT utm.utm_source, utm.utm_id, utm.source_id FROM hyjf_user.ht_utm utm
WHERE utm.source_id= #{sourceId}
) a INNER JOIN ( SELECT reg.user_id, reg.utm_id FROM hyjf_user.ht_utm_reg reg ) reg ON a.utm_id = reg.utm_id INNER JOIN ( SELECT hdpa.user_id,hdpa.accede_account AS account FROM hyjf_trade.ht_debt_plan_accede hdpa
AND hdpa.create_time >= #{dayStart}
AND hdpa.create_time <= #{dayEnd}
) hdpa ON hdpa.user_id = reg.user_id ) b GROUP BY b.source_id

SELECT CASE WHEN SUM(b.account) IS NULL THEN 0 ELSE SUM(b.account) END AS rtb_tender_price FROM ( SELECT tender.account, a.source_id, a.utm_source, reg.user_id FROM ( SELECT DISTINCT utm.utm_source, utm.utm_id, utm.source_id FROM hyjf_user.ht_utm utm
WHERE utm.source_id= #{sourceId}
) a INNER JOIN ( SELECT reg.user_id, reg.utm_id FROM hyjf_user.ht_utm_reg reg ) reg ON a.utm_id = reg.utm_id INNER JOIN ( SELECT tender.account, tender.user_id, tender.borrow_nid FROM hyjf_trade.ht_borrow_tender tender
AND tender.create_time >= #{dayStart}
AND tender.create_time <= #{dayEnd}
) tender ON tender.user_id = reg.user_id INNER JOIN ( SELECT borrow.borrow_nid FROM hyjf_trade.ht_borrow borrow WHERE borrow.project_type = 13 ) borrow ON borrow.borrow_nid = tender.borrow_nid ) b GROUP BY b.source_id

SELECT CASE WHEN SUM(b.account) IS NULL THEN 0 ELSE SUM(b.account) END AS hzr_tender_price FROM ( SELECT hct.account, a.source_id, a.utm_source, reg.user_id FROM ( SELECT DISTINCT utm.utm_source, utm.utm_id, utm.source_id FROM hyjf_user.ht_utm utm
WHERE utm.source_id= #{sourceId}
) a INNER JOIN ( SELECT reg.user_id, reg.utm_id FROM hyjf_user.ht_utm_reg reg ) reg ON a.utm_id = reg.utm_id INNER JOIN ( SELECT hct.user_id,hct.assign_capital AS account FROM hyjf_trade.ht_credit_tender hct
AND hct.create_time >= #{dayStart}
AND hct.create_time <= #{dayEnd}
) hct ON hct.user_id = reg.user_id ) b GROUP BY b.source_id

select * from ht_utm_reg a inner join ht_utm b on a.utm_id = b.utm_id left join ht_user_info c on a.user_id = c.user_id
b.source_id = #{sourceId}
c.attribute = 0

