mapper.select:
  SELECT
	DISTINCT u.mobile,u.user_id
  FROM
	hyjf_user.ht_user u
left JOIN hyjf_trade.ht_borrow_tender bt on u.user_id=bt.user_id


      AND u.bank_open_account= #{open_account,jdbcType=INTEGER}


      AND UNIX_TIMESTAMP(u.reg_time) >= #{re_time_begin,jdbcType=INTEGER}


      AND UNIX_TIMESTAMP(u.reg_time) <= #{re_time_end,jdbcType=INTEGER}


      AND UNIX_TIMESTAMP(bt.create_time)  >= #{add_time_begin,jdbcType=INTEGER}


      AND UNIX_TIMESTAMP(bt.create_time)  <= #{add_time_end,jdbcType=INTEGER}


      AND (select SUM(account) from  hyjf_trade.ht_borrow_tender where user_id=u.user_id) > #{add_money_count,jdbcType=DECIMAL}
