mapper.select:
SELECT u.user_id, u.username, ui.truename,ui.attribute, od3.`name` as regionName, od2.`name` as branchName, od.`name` as departmentName FROM ht_users u LEFT JOIN ht_users_info ui ON u.user_id= ui.user_id LEFT JOIN ht_spreads_users su ON u.user_id= su.user_id LEFT JOIN ht_crm.oa_users ou ON su.spreads_userid= ou.hyd_id AND ou.user_status IN ('E', 'Q1', 'Q11', 'Q2', 'Q21') LEFT JOIN ht_crm.oa_department od ON od.id = ou.departmentid LEFT JOIN ht_crm.oa_department od2 ON od2.id = od.parentid LEFT JOIN ht_crm.oa_department od3 on od3.id = od2.parentid WHERE 1=1
AND u.username= #{userName}
group by u.user_id

SELECT u.user_id, u.username, ui.truename,u.mobile,ui.attribute, od3.`name` as regionName, od2.`name` as branchName, od.`name` as departmentName FROM ht_user u LEFT JOIN ht_user_info ui ON u.user_id= ui.user_id LEFT JOIN ht_r_oa_users ou ON u.user_id= ou.hyd_id AND ou.user_status IN ('E', 'Q1', 'Q11', 'Q2', 'Q21') LEFT JOIN ht_r_oa_department od ON od.id = ou.departmentid LEFT JOIN ht_r_oa_department od2 ON od2.id = od.parentid LEFT JOIN ht_r_oa_department od3 on od3.id = od2.parentid WHERE 1=1
AND u.user_id= #{user_id}
group by u.user_id

SELECT u.user_id, u.username, ui.truename,u.mobile,ui.attribute, od3.`name` as regionName, od2.`name` as branchName, od.`name` as departmentName FROM ht_users u LEFT JOIN ht_users_info ui ON u.user_id= ui.user_id LEFT JOIN ht_crm.oa_users ou ON u.user_id= ou.hyd_id AND ou.user_status IN ('E', 'Q1', 'Q11', 'Q2', 'Q21') LEFT JOIN ht_crm.oa_department od ON od.id = ou.departmentid LEFT JOIN ht_crm.oa_department od2 ON od2.id = od.parentid LEFT JOIN ht_crm.oa_department od3 on od3.id = od2.parentid WHERE 1=1
AND u.username= #{userName}
group by u.user_id

SELECT u.user_id, u.username, ui.truename,u.mobile,ui.sex FROM ht_users u LEFT JOIN ht_users_info ui ON u.user_id= ui.user_id WHERE 1=1
AND u.user_id= #{user_id}
group by u.user_id

SELECT hup.user_id,user_name,age,sex,education,occupation,city,interest,interest_sum,invest_sum,recharge_sum,withdraw_sum,login_active, customer_source,ROUND((unix_timestamp(now())-last_login_time)/(60*60*24)) AS last_login_time, ROUND((unix_timestamp(now())-last_recharge_time)/(60*60*24)) AS last_recharge_time, ROUND((unix_timestamp(now())-last_withdraw_time)/(60*60*24)) AS last_withdraw_time, invest_platform,invest_age,trade_number,current_owner,add_wechat,invest_process,customer_complaint,invite_customer,remark, INSERT(hup.mobile, 3, 5, '****') as mobile FROM ht_users_portrait hup
ORDER BY hup.user_id DESC
limit #{limitStart},#{limitEnd}
