mapper.select:
SELECT hydu.user_id, hydu.username AS user_name, hydui.truename AS real_name, hydu.mobile, hydu.user_type AS type_id, hydui.role_id, hydui.attribute, hydui.birthday, hydui.sex, hydui.idcard, concat(province, city, area) AS regist_area, hydu.open_account, hydu.`status`, hydu.reg_esb, hydu.reg_time AS regTime, hydui.role_id AS user_role, hydui.attribute AS user_property, hydu.open_account AS account_status, hydu.`status` AS user_status, hydu.reg_esb AS regist_plat, hydu.user_type AS user_type, hydur.username AS recommend_name, date_format( hydu.reg_time, '%Y-%c-%d %H:%i:%s' ) AS reg_time, CASE WHEN hydui.attribute = 2 OR hydui.attribute = 3 THEN hydu.user_id ELSE hydsu.spreads_user_id END AS hyd_id, hydui.borrower_type, hydu.bank_open_account bank_open_account, hydu.inst_code AS instCode, hca.customer_id, hboa.account, date_format(hboa.create_time,'%Y-%c-%d %H:%i:%s') AS bank_open_time, CASE hydui.attribute WHEN 1 THEN d1.NAME WHEN 2 THEN dd1.NAME WHEN 3 THEN dd1.NAME END AS `regionName`, CASE hydui.attribute WHEN 1 THEN d2.NAME WHEN 2 THEN dd2.NAME WHEN 3 THEN dd2.NAME END AS `branchName`, CASE hydui.attribute WHEN 1 THEN d3.NAME WHEN 2 THEN dd3.NAME WHEN 3 THEN dd3.NAME END AS `departmentName` FROM
(SELECT * FROM ht_user ORDER BY reg_time DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}
) hydu
ht_user hydu
LEFT JOIN ht_user_info hydui ON hydu.user_id = hydui.user_id LEFT JOIN ht_spreads_user hydsu ON hydsu.user_id = hydu.user_id LEFT JOIN ht_user hydur ON hydur.user_id = hydsu.spreads_user_id LEFT JOIN ht_bank_open_account hboa ON hydu.user_id = hboa.user_id LEFT JOIN ht_certificate_authority hca ON hydu.user_id = hca.user_id LEFT JOIN ht_r_oa_users u ON u.hyd_id = hydsu.spreads_user_id AND u.user_status IN ( 'E', 'Q1', 'Q11', 'Q2', 'Q21' , 'Q4', 'Q41') LEFT JOIN ht_r_oa_department d3 ON d3.id = u.departmentid AND d3.id IS NOT NULL LEFT JOIN ht_r_oa_department d2 ON d2.id = d3.parentid LEFT JOIN ht_r_oa_department d1 ON d1.id = d2.parentid LEFT JOIN ht_r_oa_users ou ON ou.hyd_id = hydu.user_id AND ou.user_status IN ( 'E', 'Q1', 'Q11', 'Q2', 'Q21' , 'Q4', 'Q41') LEFT JOIN ht_r_oa_department dd3 ON dd3.id = ou.departmentid AND dd3.id IS NOT NULL LEFT JOIN ht_r_oa_department dd2 ON dd2.id = dd3.parentid LEFT JOIN ht_r_oa_department dd1 ON dd1.id = dd2.parentid
ORDER BY hydu.reg_time DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}

SELECT
IFNULL(SUM(a),0)
FROM (
  SELECT COUNT(*) a FROM ht_borrow_tender WHERE user_id = #{userId}
    UNION ALL
  SELECT COUNT(*) a FROM ht_borrow_tender WHERE user_id = #{userId}
    UNION ALL
  SELECT COUNT(*) a FROM ht_borrow_tender_cpn WHERE user_id = #{userId}
    UNION ALL
  SELECT COUNT(*) a FROM ht_hjh_accede WHERE user_id =  #{userId}
 ) b

SELECT id, user_id, username, real_name, mobile, idcard, `role`, `attribute`, recommend_user, is51, `status`, update_user_id, update_user, update_type, update_time, remark, borrower_type FROM ht_user_change_log l WHERE 1=1
ORDER BY l.update_time DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}
