mapper.select:
SELECT hydual.user_id, hydual.user_name, hydur.mobile, CASE WHEN hydual.auto_payment_status = 0 and (hydual.payment_cancel_time is not null or hydual.payment_cancel_time <>'' ) then 2 WHEN hydual.auto_payment_status = 0 and (hydual.payment_cancel_time is null or hydual.payment_cancel_time='') then 0 WHEN (hydual.auto_payment_status = 1) then 1 END AS auto_payment_status, CASE WHEN hydual.auto_payment_status = 0 THEN '' ELSE DATE_FORMAT(STR_TO_DATE(hydual.auto_payment_end_time, '%Y%m%d'),'%Y-%m-%d') END AS auto_payment_end_time, CASE WHEN hydual.auto_payment_status = 0 THEN '' ELSE FROM_UNIXTIME(hydual.auto_payment_time,'%Y-%m-%d') END AS auto_payment_time, bank.account, hydual.payment_max_amt FROM ht_hjh_user_auth hydual LEFT JOIN ht_bank_open_account bank ON bank.user_id = hydual.user_id LEFT JOIN ht_user hydur ON hydur.user_id = hydual.user_id
ORDER BY bank.create_time DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}

sql.where:
hydual.user_id = bank.user_id

    AND hydual.user_name LIKE CONCAT('%', #{userName}, '%')


    AND bank.account  LIKE CONCAT('%', #{bankid}, '%')


    AND hydual.auto_payment_status = #{authType,jdbcType=INTEGER} and (hydual.payment_cancel_time is null or hydual.payment_cancel_time='')


    AND FROM_UNIXTIME( hydual.auto_payment_time, '%Y-%m-%d' ) >= #{authTimeStart}
    AND auto_payment_status = 1


    AND FROM_UNIXTIME( hydual.auto_payment_time, '%Y-%m-%d' ) <= #{authTimeEnd}
    AND auto_payment_status = 1



    AND (hydual.payment_cancel_time is not null or  hydual.payment_cancel_time <>'' ) AND hydual.auto_payment_status = 0



    AND DATE_FORMAT(STR_TO_DATE(hydual.auto_payment_end_time, '%Y%m%d'),'%Y-%m-%d') >= #{signTimeStart}



    AND DATE_FORMAT(STR_TO_DATE(hydual.auto_payment_end_time, '%Y%m%d'),'%Y-%m-%d') <= #{signTimeEnd}

mapper.select:
SELECT hydual.user_id, hydual.user_name, CASE WHEN hydual.auto_repay_status = 0 and (hydual.repay_cancel_time is not null or hydual.repay_cancel_time <>'' ) then 2 WHEN hydual.auto_repay_status = 0 and (hydual.repay_cancel_time is null or hydual.repay_cancel_time='') then 0 WHEN (hydual.auto_repay_status = 1) THEN 1 END AS auto_repay_status, CASE WHEN hydual.auto_repay_status = 0 THEN '' ELSE DATE_FORMAT( STR_TO_DATE( hydual.auto_repay_end_time, '%Y%m%d' ), '%Y-%m-%d' ) END AS auto_repay_end_time, CASE WHEN hydual.auto_repay_status = 0 THEN '' ELSE FROM_UNIXTIME( hydual.auto_repay_time, '%Y-%m-%d' )END AS auto_repay_time, bank.account, hydual.repay_max_amt FROM ht_hjh_user_auth hydual LEFT JOIN ht_bank_open_account bank ON bank.user_id = hydual.user_id
ORDER BY bank.create_time DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}
