SELECT * FROM ( SELECT a.user_id, a.user_name, a.utm_id, a.utm_name, a.order_code, a.borrow_nid, a.borrow_period, a.invest_amount, DATE_FORMAT(a.invest_time,'%Y-%m-%d %H:%i:%s') as invest_time, DATE_FORMAT(a.reg_time,'%Y-%m-%d %H:%i:%s') as reg_time, CASE WHEN ifnull( ( SELECT count(id) FROM hyjf_trade.ht_borrow_tender bt WHERE bt.user_id = a.user_id AND bt.create_time < a.invest_time ), 0 ) + ifnull( ( SELECT count(assign_id) FROM hyjf_trade.ht_credit_tender ct WHERE ct.user_id = a.user_id AND ct.create_time < concat(a.invest_time) ), 0 ) + ifnull( ( SELECT count(id) FROM hyjf_trade.ht_borrow_tender_cpn bc WHERE bc.user_id = a.user_id AND bc.create_time < a.invest_time ), 0 ) + ifnull( ( SELECT count(id) FROM hyjf_trade.ht_hjh_accede ha WHERE ha.user_id = a.user_id AND ha.create_time < a.invest_time ), 0 ) = 0 THEN '1' ELSE '0' END is_first FROM ( SELECT t.user_id, u.username AS user_name, u.reg_time, utm.source_id AS utm_id, utm.utm_source AS utm_name, ten.nid AS order_code, ten.borrow_nid, CASE WHEN b.borrow_style = 'endday' THEN CONCAT(b.borrow_period, '天') ELSE CONCAT(b.borrow_period, '个月') END borrow_period, ten.account AS invest_amount, ten.create_time AS invest_time FROM ht_utm_reg t INNER JOIN ht_utm utm ON t.utm_id = utm.utm_id INNER JOIN ht_user u ON t.user_id = u.user_id INNER JOIN hyjf_trade.ht_borrow_tender ten ON t.user_id = ten.user_id AND ten.accede_order_id IS NULL INNER JOIN hyjf_trade.ht_borrow b ON ten.borrow_nid = b.borrow_nid where 1=1
AND reg_time >= DATE_FORMAT(#{registStartTime},'%Y-%m-%d %H:%i:%s')
AND reg_time <= DATE_FORMAT(#{registEndTime},'%Y-%m-%d %H:%i:%s')
AND ten.create_time >= #{investStartTime}
AND ten.create_time <= #{investEndTime}
UNION ALL SELECT t.user_id, u.username AS user_name, u.reg_time, utm.source_id AS utm_id, utm.utm_source AS utm_name, ten.accede_order_id AS order_code, ten.plan_nid AS borrow_nid, CONCAT( b.debt_lock_period, '个月' ) AS borrow_period, ten.accede_account AS invest_amount, ten.create_time AS invest_time FROM ht_utm_reg t INNER JOIN ht_utm utm ON t.utm_id = utm.utm_id INNER JOIN ht_user u ON t.user_id = u.user_id INNER JOIN hyjf_trade.ht_debt_plan_accede ten ON t.user_id = ten.user_id INNER JOIN hyjf_trade.ht_debt_plan b ON ten.plan_nid = b.debt_plan_nid where 1=1
AND reg_time >= unix_timestamp(#{registStartTime})
AND reg_time <= unix_timestamp(#{registEndTime})
AND ten.create_time >= #{investStartTime}
AND ten.create_time <= #{investEndTime}
UNION ALL SELECT t.user_id, u.username AS user_name, u.reg_time, utm.source_id AS utm_id, utm.utm_source AS utm_name, ten.assign_nid AS order_code, CONCAT('HZR', ten.credit_nid) AS borrow_nid, CONCAT(b.credit_term, '天') AS borrow_period, ten.assign_capital AS invest_amount, ten.create_time AS invest_time FROM ht_utm_reg t INNER JOIN ht_utm utm ON t.utm_id = utm.utm_id INNER JOIN ht_user u ON t.user_id = u.user_id INNER JOIN hyjf_trade.ht_credit_tender ten ON t.user_id = ten.user_id INNER JOIN hyjf_trade.ht_borrow_credit b ON ten.credit_nid = b.credit_nid where 1=1
AND reg_time >= unix_timestamp(#{registStartTime})
AND reg_time <= unix_timestamp(#{registEndTime})
AND ten.create_time >= #{investStartTime}
AND ten.create_time <= #{investEndTime}
) AS a ) mm
ORDER BY mm.invest_time DESC
limit #{limitStart} , #{limitEnd}

