select * from ( SELECT a.user_id, r.user_name as user_name, a.order_code, a.borrow_nid, a.borrow_period, a.invest_amount, r.source_name as utm_name, DATE_FORMAT(a.invest_time,'%Y-%m-%d %H:%i:%s') as invest_time, DATE_FORMAT(hu.reg_time,'%Y-%m-%d %H:%i:%s') as reg_time, CASE WHEN ifnull(( SELECT id FROM hyjf_trade.ht_borrow_tender bt WHERE bt.user_id = a.user_id AND bt.create_time < a.invest_time limit 1), 0) + ifnull((SELECT assign_id FROM hyjf_trade.ht_credit_tender ct WHERE ct.user_id = a.user_id AND ct.create_time < concat(a.invest_time) limit 1),0) + ifnull((SELECT id FROM hyjf_trade.ht_borrow_tender_cpn bc WHERE bc.user_id = a.user_id AND bc.create_time < a.invest_time limit 1),0) + ifnull((SELECT id FROM hyjf_trade.ht_hjh_accede ha WHERE ha.user_id = a.user_id AND ha.create_time < a.invest_time limit 1),0) = 0 THEN '1' ELSE '0' END is_first FROM ( SELECT ten.user_id, ten.nid AS order_code, ten.borrow_nid, CASE WHEN b.borrow_style = 'endday' THEN CONCAT(b.borrow_period, '天') ELSE CONCAT(b.borrow_period, '个月') END borrow_period, ten.account AS invest_amount, ten.create_time AS invest_time FROM hyjf_trade.ht_borrow_tender ten INNER JOIN hyjf_trade.ht_borrow b ON ten.borrow_nid = b.borrow_nid where ten.accede_order_id IS NULL
AND ten.create_time >= #{investStartTime}
AND ten.create_time <= #{investEndTime}
UNION ALL SELECT ten.user_id, ten.accede_order_id AS order_code, ten.plan_nid AS borrow_nid, CONCAT( b.debt_lock_period, '个月' ) AS borrow_period, ten.accede_account AS invest_amount, ten.create_time AS invest_time FROM hyjf_trade.ht_debt_plan_accede ten INNER JOIN hyjf_trade.ht_debt_plan b ON ten.plan_nid = b.debt_plan_nid where 1=1
AND ten.create_time >= #{investStartTime}
AND ten.create_time <= #{investEndTime}
UNION ALL SELECT ten.user_id, ten.assign_nid AS order_code, CONCAT('HZR', ten.credit_nid) AS borrow_nid, CONCAT(b.credit_term, '天') AS borrow_period, ten.assign_capital AS invest_amount, ten.create_time AS invest_time FROM hyjf_trade.ht_credit_tender ten INNER JOIN hyjf_trade.ht_borrow_credit b ON ten.credit_nid = b.credit_nid where 1=1
AND ten.create_time >= #{investStartTime}
AND ten.create_time <= #{investEndTime}
) AS a INNER JOIN ht_app_utm_reg r ON a.user_id = r.user_id INNER JOIN ht_user hu on r.user_id = hu.user_id where 1=1
and r.source_id = #{sourceId}
) tmp where 1=1
AND tmp.reg_time >= DATE_FORMAT(#{registStartTime},'%Y-%m-%d %H:%i:%s')
AND tmp.reg_time <= DATE_FORMAT(#{registEndTime},'%Y-%m-%d %H:%i:%s')
AND tmp.is_first = #{firstFlag,jdbcType=INTEGER}
AND tmp.user_name LIKE #{userName,jdbcType=VARCHAR}
AND tmp.order_code = #{orderCode,jdbcType=VARCHAR}
AND tmp.borrow_nid = #{borrowNid,jdbcType=VARCHAR}
order by tmp.invest_time desc
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}

