and r.source_id = #{sourceId}
) tmp where 1=1
AND tmp.reg_time >= DATE_FORMAT(#{registStartTime},'%Y-%m-%d %H:%i:%s')
AND tmp.reg_time <= DATE_FORMAT(#{registEndTime},'%Y-%m-%d %H:%i:%s')
AND tmp.is_first = #{firstFlag,jdbcType=INTEGER}
AND tmp.user_name LIKE #{userName,jdbcType=VARCHAR}
AND tmp.order_code = #{orderCode,jdbcType=VARCHAR}
AND tmp.borrow_nid = #{borrowNid,jdbcType=VARCHAR}
AND tmp.invest_time >= DATE_FORMAT(#{investStartTime},'%Y-%m-%d %H:%i:%s')
AND tmp.invest_time <= DATE_FORMAT(#{investEndTime},'%Y-%m-%d %H:%i:%s') order by tmp.invest_time desc
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}

select count(1) from ( SELECT a.user_id, r.user_name as user_name, a.order_code, a.borrow_nid, a.borrow_period, a.invest_amount, r.source_name as utm_name, DATE_FORMAT(a.invest_time,'%Y-%m-%d %H:%i:%s') as invest_time, DATE_FORMAT(r.register_time,'%Y-%m-%d %H:%i:%s') as reg_time, CASE WHEN ifnull( ( SELECT count(id) FROM hyjf_trade.ht_borrow_tender bt WHERE bt.user_id = a.user_id AND bt.create_time < a.invest_time ), 0 ) + ifnull( ( SELECT count(assign_id) FROM hyjf_trade.ht_credit_tender ct WHERE ct.user_id = a.user_id AND ct.create_time < concat(a.invest_time) ), 0 ) + ifnull( ( SELECT count(id) FROM hyjf_trade.ht_borrow_tender_cpn bc WHERE bc.user_id = a.user_id AND bc.create_time < a.invest_time ), 0 ) + ifnull( ( SELECT count(id) FROM hyjf_trade.ht_hjh_accede ha WHERE ha.user_id = a.user_id AND ha.create_time < a.invest_time ), 0 ) = 0 THEN '1' ELSE '0' END is_first FROM ( SELECT hbt.user_id, hbt.accede_order_id AS order_code, hbt.plan_nid as borrow_nid, CASE WHEN hb.is_month = '1' THEN CONCAT(hb.lock_period, '个月') ELSE CONCAT(hb.lock_period, '天') END AS borrow_period, hbt.accede_account AS invest_amount, hbt.create_time AS invest_time FROM hyjf_trade.ht_hjh_accede hbt LEFT JOIN hyjf_trade.ht_hjh_plan hb ON hbt.plan_nid = hb.plan_nid ) AS a INNER JOIN ht_app_utm_reg r ON a.user_id = r.user_id where 1=1
and r.source_id = #{sourceId}
) tmp where 1=1
AND tmp.reg_time >= DATE_FORMAT(#{registStartTime},'%Y-%m-%d %H:%i:%s')
AND tmp.reg_time <= DATE_FORMAT(#{registEndTime},'%Y-%m-%d %H:%i:%s')
AND tmp.is_first = #{firstFlag,jdbcType=INTEGER}
AND tmp.user_name LIKE #{userName,jdbcType=VARCHAR}
AND tmp.order_code = #{orderCode,jdbcType=VARCHAR}
AND tmp.borrow_nid = #{borrowNid,jdbcType=VARCHAR}
AND tmp.invest_time >= DATE_FORMAT(#{investStartTime},'%Y-%m-%d %H:%i:%s')
AND tmp.invest_time <= DATE_FORMAT(#{investEndTime},'%Y-%m-%d %H:%i:%s')
