mapper.select:
select

  distinct


from ht_param_name




  order by ${orderByClause}


  limit #{limitStart} , #{limitEnd}

select 

from ht_param_name
where name_class = #{nameClass,jdbcType=VARCHAR}
  and name_cd = #{nameCd,jdbcType=VARCHAR}

mapper.delete:
delete from ht_param_name
where name_class = #{nameClass,jdbcType=VARCHAR}
  and name_cd = #{nameCd,jdbcType=VARCHAR}
