sql.where:
user_id = #{userId}

AND `type` = #{type}


AND create_time >= STR_TO_DATE(#{startTime}, '%Y-%m-%d %H:%i:%s')


AND create_time <= STR_TO_DATE(CONCAT(#{endTime}, ' 23:59:59'),'%Y-%m-%d %H:%i:%s')

mapper.select:
SELECT 0 AS isTotal, DATE_FORMAT(create_time, '%Y-%m-%d %H:%i:%s' ) AS `time`, business_name AS businessName, `type`, points AS pointsDetail, DATE_FORMAT(create_time, '%m' ) AS `month`, DATE_FORMAT(create_time, '%Y' ) AS `year` FROM ht_duiba_points
ORDER BY create_time DESC
LIMIT #{limitStart} , #{limitEnd}
