mapper.select:
select

  distinct


from ht_producer_transaction_message




  order by ${orderByClause}


  limit #{limitStart} , #{limitEnd}

mapper.insert:

  SELECT LAST_INSERT_ID()

insert into ht_producer_transaction_message (msg_id, body, message_status, 
  retry_times, topic, tags, 
  `keys`, create_time, update_time
  )
values (#{msgId,jdbcType=VARCHAR}, #{body,jdbcType=VARCHAR}, #{messageStatus,jdbcType=TINYINT}, 
  #{retryTimes,jdbcType=INTEGER}, #{topic,jdbcType=VARCHAR}, #{tags,jdbcType=VARCHAR}, 
  #{keys,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )

