mapper.select:
SELECT crm.id, crm.`day`, crm.`week`, crm.interest_wait_total, crm.interest_yes_total, IFNULL(crm.interest_yes_total, 0) - IFNULL(crm.interest_wait_total, 0) AS repay_gap, crm.update_time FROM ht_coupon_repay_monitor crm WHERE crm.del_flag = 0
AND crm.`day` >= #{timeStartSrch}
AND crm.`day` <= #{timeEndSrch}
ORDER BY UNIX_TIMESTAMP(crm.day) DESC
LIMIT #{limitStart,jdbcType=INTEGER} , #{limitEnd,jdbcType=INTEGER}

SELECT count(crm.id) FROM ht_coupon_repay_monitor crm WHERE crm.del_flg = 0
AND crm.`day` >= #{timeStartSrch}
AND crm.`day` <= #{timeEndSrch}
ORDER BY crm.`day` DESC

SELECT SUM(IFNULL(crm.interest_wait_total, 0)) AS interest_wait_total_all, SUM(IFNULL(crm.interest_yes_total, 0)) AS interest_yes_total_all FROM ht_coupon_repay_monitor crm WHERE crm.del_flag = 0
AND crm.`day` >= #{timeStartSrch}
AND crm.`day` <= #{timeEndSrch}
