sql.where:
el.del_flg = 0
AND el.`status` <> 0

	AND el.order_id LIKE CONCAT('%', #{orderId}, '%')


	AND u.username LIKE CONCAT('%', #{username}, '%')


	AND el.`type` = #{type}


	AND FROM_UNIXTIME( el.add_time, '%Y-%m-%d' ) >= #{timeStartSrch}


	AND FROM_UNIXTIME( el.add_time, '%Y-%m-%d' ) <= #{timeEndSrch}

mapper.select:
SELECT el.uuid, el.order_id, CASE WHEN el.type = 0 THEN '体验金收益回款' WHEN el.type = 1 THEN '加息券收益回款' WHEN el.type = 2 THEN '代金券收益回款' ELSE '' END trade_type, el. STATUS, CASE WHEN el. STATUS = 1 THEN '失败' WHEN el.STATUS = 2 THEN '失败' WHEN el.STATUS = 0 THEN '成功' END trade_status, el.respcode, el.trans_amt, el.cus_id, el.acct_id, el.user_id, u.username, el.remark, FROM_UNIXTIME( el.add_time, '%Y-%m-%d %H:%i:%S' ) AS add_time_view, el.add_user, el.update_time, el.update_user, el.del_flag FROM ht_transfer_exception_log el LEFT JOIN ht_r_user u ON u.user_id = el.user_id
ORDER BY update_time DESC
LIMIT #{limitStart} , #{limitEnd}
