mapper.select:
SELECT b.borrow_nid AS borrowNid, b.account AS borrowAccount, b.borrow_apr AS borrowInterest, b.account - hba.service_fee AS yesAccount, FROM_UNIXTIME(b.recover_last_time,'%Y-%m-%d %H:%i:%S') AS yesAccountTime, hbr.repay_capital AS repayCapital, hbr.repay_interest AS repayInterest, hbr.repay_account + hbr.repay_fee AS borrowTotal, hbr.repay_account_all AS repayTotal, CASE WHEN hbr.repay_status = 1 THEN FROM_UNIXTIME(hbr.repay_action_time,'%Y-%m-%d %H:%i:%S') ELSE 0 END AS repayYseTime, CASE WHEN b.borrow_style = 'endday' OR b.borrow_style = 'end' THEN 1 ELSE b.borrow_period END AS periods, hhpa.asset_id AS productId, hbr.repay_status AS repayStatus, hbr.repay_fee AS manageFee FROM ht_borrow_repay hbr INNER JOIN ht_borrow b ON b.borrow_nid = hbr.borrow_nid INNER JOIN ht_borrow_info hbi ON b.borrow_nid = hbi.borrow_nid INNER JOIN ht_borrow_apicron hba on hba.borrow_nid = b.borrow_nid and hba.api_type = 0 and hba.status = 6 INNER JOIN ht_hjh_plan_asset hhpa ON hhpa.inst_code = hbi.inst_code and b.borrow_nid = hhpa.borrow_nid WHERE hbi.inst_code = #{instCode}
AND hbr.repay_status = #{repayType}
AND hbr.repay_time >= UNIX_TIMESTAMP(#{startDate})
AND hbr.repay_time <= UNIX_TIMESTAMP(#{endDate})
AND ( b.borrow_style = 'principal' OR b.borrow_style = 'month' OR b.borrow_style = 'endmonth' )
AND hhpa.asset_id = #{productId}
ORDER BY hbr.id ASC
limit #{limitStart} , #{limitEnd}

SELECT COUNT(*) FROM ht_borrow_repay hbr INNER JOIN ht_borrow b ON b.borrow_nid = hbr.borrow_nid INNER JOIN ht_borrow_info hbi ON b.borrow_nid = hbi.borrow_nid INNER JOIN ht_borrow_apicron hba on hba.borrow_nid = b.borrow_nid and hba.api_type = 0 and hba.status = 6 INNER JOIN ht_hjh_plan_asset hhpa ON hhpa.inst_code = hbi.inst_code and b.borrow_nid = hhpa.borrow_nid WHERE b.inst_code = #{instCode}
AND hbr.repay_status = #{repayType}
AND hbr.repay_time >= UNIX_TIMESTAMP(#{startDate})
AND hbr.repay_time <= UNIX_TIMESTAMP(#{endDate})
AND ( b.borrow_style = 'principal' OR b.borrow_style = 'month' OR b.borrow_style = 'endmonth' )
AND hhpa.asset_id = #{productId}
