select b.borrow_nid,u.mobile,ui.truename,ui.idcard,b.repay_status,0 status,b.nid, (select b1.repay_period from ht_borrow_repay_plan b1 where b1.borrow_nid=b.borrow_nid and b1.repay_time <#{repayTime} and b1.repay_time >#{repayTimeStart} b1.repay_status=0 order by b1.repay_period desc limit 1)repay_period, (select FROM_UNIXTIME(b2.repay_time,'%Y-%m-%d') from ht_borrow_repay_plan b2 where b2.borrow_nid=b.borrow_nid and b2.repay_time <#{repayTime} and b2.repay_time >#{repayTimeStart} and b2.repay_status=0 order by b2.repay_period desc limit 1)repay_time, (select b4.repay_account from ht_borrow_repay_plan b4 where b4.borrow_nid=b.borrow_nid and b4.repay_time <#{repayTime} and b4.repay_time >#{repayTimeStart} and b4.repay_status=0 order by b4.repay_period desc limit 1)repay_account, (select bb.repay_account+bb.repay_fee+b.delay_interest from ht_borrow_repay_plan bb where bb.borrow_nid=b.borrow_nid and bb.repay_time <#{repayTime} and bb.repay_time >#{repayTimeStart} and bb.repay_status=0 order by bb.repay_period desc limit 1)targetAmount, (select b7.delay_days from ht_borrow_repay_plan b7 where b7.borrow_nid=b.borrow_nid and b7.repay_time <#{repayTime} and b7.repay_time >#{repayTimeStart} and b7.repay_status=0 order by b7.repay_period desc limit 1)delay_days, (select ii.borrow_style from ht_borrow ii where ii.borrow_nid = b.borrow_nid) borrow_style, date_format(NOW(),'%Y-%m-%d %H:%i:%s') confirmStatusTime,'0.00' repay_account_all from ht_borrow_repay_plan b inner JOIN ht_r_user u on u.user_id=b.user_id inner JOIN hyjf_user.ht_user_info ui on u.user_id=ui.user_id where b.repay_status=0 and b.repay_time <#{repayTime} and b.repay_time >#{repayTimeStart} and b.borrow_nid in
#{item}
group by borrow_nid
